css:
@-moz-document domain("trello.com") {
body {
  /*background-color: #eee !important;*/
  /*background-image: url('https://trello-attachments.s3.amazonaws.com/54a1a57c378fa86551d5383b/1920x1200/da44714a61f45401c28c69506b1e29d3/moon-greyscale-wallpaper-background-winter-space-35140.jpg') !important;*/
    font-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;
    font-weight: 300 !important;
}

h3 {font-weight:300 !important}

.header-woof {display:none !important}

#header {
    background: rgb(14, 116, 175) !important;
    padding: 10px !important;
}

.board-header-btn-name-org-logo {
 -webkit-filter:  contrast(3000%) invert(100%) ; 
}

.js-open-board  {
  background:#979798 !important;
  font-weight:300 !important;;

}
#board {
  padding: 1em;
box-sizing: border-box;
height: calc(97% - 1em);
}

.js-open-board:hover  {background:#F45B5B !important}
.thumbnail {background:#979798 !important}

.sidebar-section-header-title {text-decoration:none !important;font-weight:300 !important}
#search-text {border-radius:0px !important}

.board-header {margin-bottom:20px !important; padding-bottom: 0px !important}

/* header buttons */
.header-btn.header-notifications, .header-btn.header-boards, 
.header-btn.header-search, .header-btn.header-login, 
.header-btn.header-signup {
    background: none !important;
}

.header-btn:hover {
    background: none !important;
}

.header-user {
    top: 10px !important;
    right: 5px !important;
    font-weight:300 !important
}

.board-header {
    height: 40px !important;
    background: rgb(14, 116, 175) !important;
}
.board-widgets{
  background: rgba(250,250,250,0.2);
}
.sidebar-hide-btn .icon-sm{
	background: rgba(250,250,250,0.2);
}

.header-btn-text {font-weight:300 !important}


.board-header-btn-text {
    text-decoration: none !important;
}

.board-header-btn-name  {font-weight:300 !important}

.text {
    text-decoration: none !important;
}

.header-member {
    background: none !important;
}

.member {
    border-radius: 20px !important;
    border: none !important;
    background: none !important;
}

.member img {
    border-radius: 0 !important;
    border: none !important;
}

.list-area {
    margin-top: 30px !important;
}
.list-header {
	padding: 1em !important;
	margin: 1em 0 !important;
	box-shadow: 0 3px 7px -3px rgba(0,0,0,0.3) inset;
	color: rgba(0,0,0,0.5) !important;
	background: rgba(255,255,255,0.2) !important;
}
.list-header-name {
  	font-weight: bold !important;
    text-transform: capitalize;
}

.card-label {
	width: 2em !important;
    height: 2em !important;
	padding: 0px !important;
	box-shadow: -1px -2px 6px -2px black inset;
}
.card-label-selectable {
	width: 100% !important;
}
.list {
    background: transparent !important;
	padding: 0.5em !important;
	flex: 0 0 420px !important;
	margin: -0.5em 0.5em !important;
}
.open-card-composer {
	color: white !important;
	padding: 2em 1em 1em !important;
}

.list:nth-child(1) .list-card{background-color: rgb(200,99,99) !important;}
.list:nth-child(2) .list-card{background-color: rgb(255,180,50) !important;}
.list:nth-child(3) .list-card{background-color: rgb(92,174,120) !important;}

.list-card{
  border: 0 !important;
    border-radius: 0 !important;
    box-shadow: 0 3px 7px -3px;
  max-width: 360px;
  margin: 1.5em auto;
  top:0;
  transition: 700ms;
}
.list-card.active-card{
  box-shadow: 0 7px 15px 0px;
  max-width: 390px;
  top: -7px;
}
.list-card-title{
  max-width: 320px;
  text-align: center;
  margin: auto;
}
.list-card-title, .list-card-details {
  padding: 0.4em 0.7em 1em 0.7em;
  color: white !important;
}
  .list-card-members{ margin:0; }
.list-card-members>*{ margin:0 0.25em; }
.active-card .list-card-operation{
	opacity: 1;
	transition-delay: 1s;
}
.list-card-operation{
	background-color: rgb(10,150,150) !important;
    border-radius: 100% !important;
    box-shadow: 0 3px 7px -2px black;
    transform: translate(-40%,-40%);
    color: white !important;
	display: block;
	opacity: 0;
	transition: opacity 200ms easy-out;
	transition-delay: 0s;
}
.badge>*{
  color: white !important;
}
.member{
	box-shadow: 0 0 0 2px white;
}
.member>*{
  background-color: white !important;
}
.list-cards{
  padding: 1.5em 0;
  box-shadow: 0 0 20px -5px rgba(0,0,0,0.3) inset;
}
.list-cards::-webkit-scrollbar {
	width: 0px !important;
}
.list-card:hover {transition: background 0.5s ease-out !important}
.add-list{
  flex: 0 0 90px;
 }
.add-list:hover {
  background: rgb(14, 116, 175) !important;
}
.quick-card-editor {
	background: rgba(14, 116, 175, 0.5) !important;
}
.quick-card-editor .quick-card-editor-buttons-item {
	padding: 0.5em;
	background-color: rgb(10,150,150) !important;
    border-radius: 100% !important;
    box-shadow: 0 3px 7px -2px black;
    }
.quick-card-editor .quick-card-editor-buttons-item-text {
	display: none;
}
.open-card-composer:hover {
    text-decoration: none !important;
    background: none !important;
    color: white !important;
}

.window-wrapper {
    background: #fff !important;
}

.window-wrapper h3 {font-weight:normal !important}

.window-wrapper a {text-decoration:none !important}


.window-overlay {
    background: rgba(14, 116, 175, 0.5) !important;
}

strong {font-weight:300 !important}

.js-open-move-from-header {color:white !important; font-weight:300 !important}
.window-title-text {font-weight:300 !important}

.button-link {font-weight:300}

.button-link:hover {
    background: #F45B5B !important;
    border-color:  #F45B5B !important;
} 

.checklist-progress-bar-current {background:#F45B5B !important}

.confirm {background:#F45B5B !important}
.ui-state-active {background:#F45B5B !important}
}
