css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("qt-project.org") {
  
  .qt13a-no-bg-img, body, .col-main-container {
   background-color: #000 !important;
   background-image: none !important;
  }
  
  #qt13a-header p.qt13a-title a {
    font-weight: 600;
    color: #111;
}
  #qt13a-header .qt13a-nav ul li a {
    color: #111;
    margin: 0px;
    padding: 0px;
    line-height: 18px;
    font-size: 18px;
}
  .block-content:after, .block-list li.item:after, .header .quick-access:after, .clearer:after, .header:after, .header-panel:after, #nav:after, .main:after, .footer-panel:after, .breadcrumbs ul:after, .footer:after, .col2-set:after, .col3-set:after, .col4-set:after, .search-autocomplete li:after, .block li.item:after, .block-cart .actions:after, .block .actions:after, .cms-index-index .block-featured li.item:after, .field-100:after, .group-select li:after, .group-select .field:after, .form-list li:after, .clearfix:after, .col-main-wrapper{
    background-color: #222 !important;
    color: #EEE !important;
  }
  
  .col2-right-layout .col-right {
   width: 256px;
  }
  
  .breadcrumbs, .breadcrumbs .product {
    background-image: none !important;
    background-color: #666 !important;
    color: #FFF !important;
  }
  
  .breadcrumbs a {
    color: #CCC !important;
  }
  
  span.preprocessor, span.operator{
    color: #DDD !important;
  }
  
  span.comment, .geshi .co1, .geshi .coMULTI {
    color: #00CC00 !important;
  }
  
  span.keyword, .geshi .kw1, .geshi .kw2, .geshi .kw3, .geshi .kw4 {
    font-weight: bold;
    color: #00B9BC !important;
  }
  
  span.operator {
    color: #CCCC00 !important;
  }
  
  div.cpp-qt.geshi ol li div {
    color: #EEE;
  }
  
  .qtdocs, ul .qtdocs{
    background-color: #222 !important;
    color: #EEE !important;
  } 
  
  .qtdocs h1, .qtdocs h2, .qtdocs h3, .qtdocs h4, .qtdocs h5, .qtdocs h6 {
   color: #AA9 !important;  
  }
  
  .qtdocs h3.fn {
    background-color: #444 !important;
  }
  
  .qtdocs .toc {
    background-color: #333 !important;
  }
  
  .qtdocs .toc a, .sidebar a, #id-col-main-wrapper, .col-main-wrapper-secondary a{
   color: #4CF !important;
  }
  
  .qtdocs pre, .qtdocs td, .qtdocs table{
    background-color: #444 !important;
    color: #EEE !important;
  }
  
  .qtdocs a, .geshi .kw5 {
    color: #0AF !important;
  }
  
  .qtdocs th {
    color: #000 !important;  
  }
  
  span.name {
    color: #CCC;
  }
  
  .clearfix {
    background: none !important;
  }
  .note-wrapper .note-contents {
    background-color: #444 !important;
    color: #AA9 !important;
  }

  .note-wrapper:nth-child(2n) .note-rate-box {
    background-color: #BBB;
  }
  
  #comments .author_stats p {
    color: #EEE !important;
  }
  
  .qtcdk {
    color: #EEE !important;
  }
  
  .sidebar .block-square .square-content{
   background-color: #444 !important;  
    margin-left: 10px !important;
    border: 1px solid #DADADA !important;
    color: #EEE !important;
  }
  
  .sidebar h2 {
    background-color: #444 !important;  
    color: #EEE !important;
  }
  .sidebar .socialwrap a.toggle{
    background-color: #666 !important;
  }
  
  .sidebar-shadow {
    border: 1px solid #DADADA !important;
    background-color: #000 !important;
    box-shadow: 3px 3px 3px #242424 !important;
  }
  
  .ratebox a{
    color: #FFF !important;
  }
  
  .sidebar .block, .sidebar ul.form-list label, .sidebar .block-profile p.login, .sidebar p.openid-link, .sidebar p.forgot-link,
  .sidebar .block .block-content p{
    background-color: #444 !important;
    color: #EEE !important;
  }
  
  .qtcdk ol li *, .wiki-content ol li *, .forum_post_body ol li *, .forum_post_body_replies_inner ol li * {
    color: #EEE !important;
  }
  
  #tags .tags_list a.tag, .tag-name a.tag {
    background-color: #666 !important;
  }
  
  #qt13a-header .qt13a-gradient {
    margin: 0px;
    min-height: 126px;
    padding: 0px;
}
  
.forum_post_body p {
    color: white;
}
  
.forum_post_body h1, h2, h3 {
    color: #AA9;
}  
  
.forum_post_body_replies_inner {
  color:white;
}
  
}
