css:
@-moz-document regexp('(https?:\/\/)anilist\.co\/(?!animelist|mangalist).*'){

body {
	background: url("/*[[BG]]*/") repeat fixed 50% 0% rgba(0, 0, 0, 0);
	color: #F5F5F5;
}
time-ago {
	color: #D2D2D2 !important;
}
#nprogress .bar {
	background: #E80015;
}
.activity_wrap .activity__body, .thread__body, .forum__thread__comments .comment .comment__body {
	background: rgba(26, 26, 26, 0.5);
	background-image: 
	-webkit-linear-gradient(-45deg, rgba(0,0,0,0.26) 0%,
	rgba(0,0,0,0.26) 25%,
	rgba(36,36,36,0) 25%,
	rgba(12,12,12,0) 75%,
	rgba(0, 0, 0, 1) 100%);
	background-image: 
	-moz-linear-gradient(-45deg, rgba(65,65,65,0.3) 0%,
	rgba(65,65,65,0.3) 25%,
	rgba(36,36,36,0) 25%,
	rgba(12,12,12,0) 75%,
	rgba(0, 0, 0, 1) 100%);
	border: none;
}
.activity_wrap .activity__body .user_list, .thread__title, .forum__thread__comments .comment .comment__body .comment__name, .thread__tags a li:first-of-type{
	border-bottom: 1px solid #860909;
}
.activity_wrap .activity__nav {
	background: rgba(0, 0, 0, 0.8);
	border-top: 1px solid #2A2A2A;
}
.activity_wrap a, .thread__title, .forum__thread__comments .comment .comment__body .comment__name, #markEditor button, .comment-write .mark-live .mark-live__header, .comment-write .mark-live .mark-live__header .btn, .series_section h3, .char__title, .character-content h3, .series__activity a, .char-header h1, .thread__tags a li:first-of-type {
	color: #0179A5;
}
.activity_wrap .activity__nav .activity__replies .activity__reply {
	border-bottom: solid 1px black;
	background: rgba(16, 16, 16, 0.8);
}
.user_list {
	color: #F5F5F5;
}
.activity__new {
	background: #860909;
	padding-top: 20px;
	left: -26px;
	top: 0px;
	-webkit-border-radius:0px;
	-moz-border-radius:0px;
	-ms-border-radius:0px;
	-o-border-radius:0px;
	border-radius: 0px;
	box-shadow: inset 0px 4px 1px rgba(0,0,0,0.5);
}
.activity__new:after, .activity__new:before {
	border-top: 15px solid #860909; 
	bottom: -15px;
	content: '';
	height: 0;
	position: absolute;
	width: 0;
}
.activity__new:after {
	border-right: 25px solid transparent;  
	left: 0;  
}
.activity__new:before {
	border-left: 25px solid transparent;  
	right: 0;  
}

/*==================================================NavBar===============================================*/
nav {
	background: rgba(255, 255, 255, 0.1) !important;
}
nav a:hover {
	background: rgba(0, 0, 0, 0.18) !important;
}
a:hover {
	color: #DB3434 !important;
}
nav .nav__footer {
	background: #5A0000;
}
footer .info {
	background: #5A0000;
}
footer .recent {
	background: rgba(0, 0, 0, 0.5);
}
.noti {
	background: rgb(25, 25, 25);
	max-width: 330px;
	height: 280px;
	border: solid 1px #5E5E5E;
}
.noti .noti_row {
	background: rgba(25, 25, 25, 0.99);
	border-bottom: 1px solid #2A2A2A;
}
.noti .fa-times {
	margin-left: 298px;
	color: #697381;
}
.noti .noti_row:hover {
	color: inherit;
	background: #303030;
}
nav a[href*="/animelist"] .fa-th-list:before {
    content: "\f108"
}
nav a[href*="/mangalist"] .fa-th-list:before {
    content: "\f02d"
}
.fa-comments:before {
	content: "\f0e6" !important;
}
.fa-comment:before {
	content: "\f0e5" !important;
}
/*==================================================Home==================================================*/
.home > .clearfix {
	background: rgba(45, 45, 45, 0.5);
    margin-top: 20px;
	margin-bottom: 20px;
    box-shadow: 0px 2px 4px #000;
}
.home h3, .browse h3 {
	color: #E8E8E8;
	margin-bottom: 10px;
	margin-top: 0px;
	padding: 5px;
	padding-left: 14px;
	background: rgb(134, 9, 9);
    text-shadow: 0px 2px 4px #000000;
}
.home .series__forum {
	margin-bottom: 20px;
	box-shadow: 0px 2px 4px #000;
}
.home .airing__wrap {
	margin-right: 8px;
	margin-left: 10px;
}
.home .airing__title {
	-webkit-transition: opacity 0.2s ease 0s;
	-moz-transition: opacity 0.2s ease 0s;
	-o-transition: opacity 0.2s ease 0s;
	transition: opacity 0.2s ease 0s;
	visibility: hidden;
}
.home .cover:hover .airing__title {
	opacity: 1;
	-webkit-transition-delay: 0s;
	-moz-transition-delay: 0s;
	-o-transition-delay: 0s;
	transition-delay: 0s;
	visibility: visible;
}
.home .series_section h3 {
	margin-bottom: 0px;
}
.forum__row.clearfix {
	border: none;
	margin-bottom: 0px;
}
.forum__row a {
	color: #C5C5C5;
}

/*=================================================Profile================================================*/
.banner {
	box-shadow: 2px 6px 4px rgba(0, 0, 0, 0.8);
}
.fav {
	width: 107%;
	background: rgba(0, 0, 0, 0.8);
}
.user_stats div:first-of-type {
	border-right: 1px solid #1D1D1D;
}
.fav .fav__edit, .user_stats .list_stats_btn {
	background: rgba(21, 21, 21, 1);
}
.fav .fav__save {
	background: #380000;
	opacity: 0.5;
}
.fav h1 {
	font-size: 1.3em;
	color: #700202;
}
.fav h2 {
	padding-left: 5px;
}

/*=================================================Search=================================================*/
form.search {
	background-color: rgba(255, 255, 255, 0.1);
	border-bottom: solid 3px #8E0000;
}
.search .search__options {
	width: auto;
	padding-left: 10px;
}
.search select {
	background: rgb(71, 71, 71);
}
.search input {
	background: transparent !important;
	border: none !important;
	color: #FFFFFF;
	font-size: 1.4em;
	padding: 10px;
	width: 50%;
}
.search.col-sm-10.col-sm-offset-1.clearfix {
	background: rgba(82,122,206,0.05);
	margin-top: 15px;
}
.search .cover_wrap {
	color: #F5F5F5;
	margin: 1.1em;
}

/*=================================================Forum=================================================*/
.forum-header {
	left: 4.999999%;
	position: relative;
	margin-top: 0px;
	background: rgba(27, 27, 27, 0.99);
	width: 90%;
	padding-top: 5px;
	padding-bottom: 2px;
	border: 1px solid rgb(102, 0, 0);
	border-top: none;
	border-bottom-right-radius: 8px;
	border-bottom-left-radius: 8px;
}
.forum__nav {
	background: rgba(255, 255, 255, 0);
	margin-left: 8.499995%;
	padding-left: 0px;
}
.forum__nav a {
	border-left: solid 1px #3A3A3A;
}
.forum__nav input {
	background: rgba(38, 38, 38, 0.99);
	border: solid 1px #3A3A3A;
	border-radius: 2px;
	top: 0px;
	color: #F5F5F5;
	padding: 0px;
}
.query-tags {
	color: #A5A5A5;
	font-size: 1em;
	margin-right: 0px;
	margin-top: -44px;
}
.query-tags .create-thread {
	background: #860909;
	border: none;
}
.query-tags .create-thread:hover {
	background: #860909;
}
.query-tags:before {
	border-left: solid 7px #860909;
}
.tag-panel span {
	margin-right: 4px;
	margin-top: 8px;
}
.tag.tag--anime {background: #DB4545;}
.tag.tag--manga {background: #961616;}
.forum__row {
	background: rgba(45, 45, 45, 0.5);
	border: none;
	color: #D37E7E;
	border-radius: 0;
}
.forum .forum__row time-ago {
	margin-left: 15px;
	width: 90px;
}
.forum__row .row__counts {
	margin-left: 10px;
	width: 50px;
}
.forum .forum__feed .forum__row:nth-child(2n+1) {
	background-color: rgba(45, 45, 45, 0.3);
}
.thread__body {
	border: none;
}
.thread__body__tags {
	border-top: 1px solid #2A2A2A;
	padding-top: 8px;
}
.tag {
	background: #414141;
}
.thread__user span {
	color: #E4E4E4;
	padding: 2px;
	background: #860909;
	margin-top: 5px;
	border-radius: 2px;
}
.forum__thread .reply, .reverse-btn{
	background: #380000;
	color: #E5E5E5;
}
.reverse-btn{
	border: solid 1px #5F0000;
}
.forum__thread .reply {
	border-left: solid 1px #5F0000;
	border-top: solid 1px #5F0000;
	border-radius: 2px 0 2px 0;
}
.forum__thread__comments .comment .comment__body span {
	padding: inherit;
}
.forum__thread__pagination {
	background: rgba(0, 0, 0, 0.5);
    border: none;
    color: inherit;
    margin-right: 0;
}
.forum__thread__pagination .current_page {
	background: #860909;
}
.pageTitle {
	padding-left: 4px;
}
.pageTitle hr {
	margin-right: 0;
	margin-left: 65px;
}
.thread__tags a {
	background: rgba(0, 0, 0, 0.5);
	border: solid 1px #3A3A3A;
}
.forum-tags .tag__section {
	background: rgba(38, 38, 38, 1);
	border: solid 1px #3A3A3A;
}
.forum-tags .tag__section .tag__user .tag__user__thread {
	width: 100px;
	text-align: left;
}
.forum-tags .tag__section span:first-of-type {
	color: #0043A5;
}
/*============================================Setting==============================================*/
.settings {
	background: rgba(0, 0, 0, .3);
}
.settings__nav {
	background: rgb(134, 9, 9);
}
.settings__nav .active {
	background: #A10707;
}
.settings__nav a {
	border-right: solid 1px #6F0000;
}
select {
	background: #252525;
	border: solid 1px #3F3F3F;
}
.settings .settings__mal form {
	background: rgba(38, 38, 38, 0.99);
	border: solid 1px #3A3A3A;
}
/*============================================Submit-form==========================================*/
.commentExpand #markEditor .markEditor {
	padding-bottom: 50px;
}
.comment-write {
	border-top: solid 1px #3A3A3A;
}
input[type=text], input[type=password], input[type=number], textarea, .listNotes, .adr, .update select {
	border: solid 1px #3A3A3A;
}
#markEditor .btn-group, #markEditor button, 
.comment-write .mark-live .mark-live__header, 
.comment-write .mark-live .mark-live__header .btn, 
.rootSeriesEdit .popup, 
.adr {
	background: rgba(23, 23, 23, 0.99);
}
#markEditor button:hover, .comment-write .mark-live .mark-live__header .btn:hover {
	color: #00B0F2;
}
input[type=submit], button[type=submit] {
	background-color: #0479A5;
}
input[type=text], input[type=password], input[type=number], textarea, .comment-write .mark-live, .update select {
	background: rgba(38, 38, 38, 0.99);
}
.rootSeriesEdit .fa-times {
	color: #800;
}
/*============================================Anime-Manga==========================================*/
.series-data {
	background: rgba(0, 0, 0, 0.8);
}
.series_section h3 {
	border-bottom: 1px solid #860909;
}
.char__role {
	color: #989898;
}
.rel--img .label, .rel--img .label--alternative:after, .rel--img .label--sequel:after, .rel--img .label--prequel:after, .rel--img .label--other:after, .rel--img .label--summary:after, .rel--img .label--adaptation:after, .rel--img .label--side:after, .rel--img .label--character:after, .rel--img .label--parent:after, .rel--img .label--spin:after {
	border-top: none;
}
.series_section .char__row:nth-of-type(2n) {
	border-left: solid 1px #3A3A3A;
}
.series__activity div {
	border-bottom: solid 1px #3A3A3A;
}
.series__activity .activity__data {
	font-size: 0.8em;
}
.char-header {
	background: rgba(0, 0, 0, 0.7);
	border-bottom: none;
}
.char-header .favBtn {
	color: #75FF31;
}
.character-content__anime .char__row:nth-of-type(2n) {
	background: rgba(110, 110, 110, 0.1);
}
.staff-content .char__row {
	background: rgba(110, 110, 110, 0.05);
	border-bottom: solid 1px #242424;
}
.lang-tabs {
	background: rgba(110, 110, 110, 0.1);
	border-bottom: solid 1px #3A3A3A;
}
.lang-tabs .selected {
	border-bottom: solid 1px #DB3434;
}
/*============================================Browse==========================================*/
.browse .browse__section {
	background: rgba(45, 45, 45, 0.5);
	margin-top: 30px;
	text-align: center;
	border-radius: 8px;
}
.browse .cover_wrap {
	margin: 10px 10px 0 10px;
}

/*============================================Others==========================================*/
::-webkit-scrollbar-track-piece {background-color: #030304;}
::-webkit-scrollbar-thumb {background-color: #5A0008;}
::-webkit-scrollbar-thumb:hover {background: #860909;}

/****add new class****/
.pre-scrollable {
	max-height: 340px;
	overflow-y: scroll;
}
}
