css:
@-moz-document regexp("^http://anilist.co/?(?:(?!animelist|mangalist).)*$") {
body {
	font-size: 100%;
	font-weight: normal;
	overflow-y: scroll;
	background: rgb(32, 32, 32);
	background-size: cover;
	background-position: center center;
	background-attachment: fixed;
	background-repeat: no-repeat;
	color: rgba(255, 255, 255, 0.88);
}

#container-main {
	margin: 0 !important;
}

#view {
	font-size: 115%;
	margin: 0 !important;
	margin-top: 50px !important;
}

nav:hover+#container-main {
	margin-left: 0px !important;
}

nav {
	width: 100% !important;
	height: 50px !important;
	min-height: 50px !important;
	background: linear-gradient(to bottom, rgb(24, 24, 24) 0%, rgb(16, 16, 16) 100%) !important;
}

.nav__footer {
	display: none;
	padding: 0;
	bottom: inherit;
	right: 0;
	top: 0;
}

nav:hover {
	/*width: inherit;
	min-height: auto !important;*/
}

nav a {
	float: left !important;
	width: 50px !important;
	height: 50px !important;
	padding: 14px;
}

nav a:hover {
	background: transparent;
}

nav a span {
	display: none !important;
}

nav br {
	display: none;
}

a,
.forum__row__title {
	color: rgba(255, 255, 255, 0.6) !important;
}

a:hover,
.forum__row__title:hover,
.activity_wrap .activity__nav .reply:hover,
.activity_wrap .activity__nav .replies:hover {
	color: rgba(255, 255, 255, 1.0) !important;
	text-shadow: 0 0 8px rgba(255, 255, 255, 0.4);
}

h1,
h2 {
	color: rgb(254, 254, 254) !important
}

h3 {
	color: rgba(255, 255, 255, 0.88) !important;
	text-align: right;
	border: none !important;
}

.lang-tabs {
	background: none !important
}

footer {
	height:0px !important;
	margin-top:0px !important;
}

footer .recent {
	background: rgba(0, 0, 0, 0.1)
}

.logo__wrap {
	display: none
}

.browse__section {
	color: rgba(255, 255, 255, 0.8)
}

.activity_wrap {
	color: rgba(255, 255, 255, 0.8)
}

.activity__body,
.activity__nav,
.comment__body,
.thread__body,
.user_stats,
.fav,
.fav__edit,
.series-data,
.series_section {
	background: transparent !important;
	border: none !important;
	color: rgb(240, 240, 240) !important;
}

.activity__body div {
	border: none
}

.activity__body a {
	color: rgba(255, 255, 255, 0.8)
}

.activity__nav {
	border: none !important
}

.activity__nav time-ago {
	opacity: 0.1 !important
}

.activity__reply,
.noti,
.noti .noti_row {
	background: transparent !important;
	border: none !important;
}

.noti {
	max-width: 500px;
	background: rgba(4, 4, 4, 0.95) !important;
}

.noti .fa-times {
	margin-left: 484px;
	margin-top: -24px;
	color: white;
}

.activity__reply markdown {
	padding: 1.5em 1em
}

.activity__reply__name {
	color: rgba(255, 255, 255, 0.9) !important;
	margin-top: 9px;
	margin-left: 3px;
}

.activity__img div {
	width: 73px !important;
	height: 73px !important;
}

.activity__reply__avatar div,
.activity__img div {
	border-radius: 100% !important
}

.activity_wrap .activity__nav .reply,
.activity_wrap .activity__nav .replies {
	color: inherit
}

.activity__new {
	width: 38px;
	height: 38px;
	text-align: center;
	background: rgba(0, 0, 0, 0.1);
	border-radius: 100%;
	left: -50px;
}

.comment__avatar {
	border-radius: 100% !important
}

.comment__body markdown {
	color: rgb(240, 240, 240) !important
}

.comment__name {
	border: none !important
}

.comment__name time-ago {
	color: rgba(255, 255, 255, 0.2) !important
}

.user_list {
	border: none !important
}

#view {
	color: rgb(16, 16, 16)
}

.forum {
	width: 60%;
	left: 20%;
	background-color: rgba(16, 16, 16, 0.1) !important;
	border-radius: 10px;
}

.forum-header {
	width: 100%;
	left: 0;
	right: 0;
}

.forum__nav {
	background: transparent;
	height: auto;
}

.forum__nav a {
	padding: 1em
}

.forum__feed {
	/*box-shadow: 1px 1px 16px rgba(0, 0, 0, 0.6);_NO__DOTCOMMA__AFTER___NO__DOTCOMMA__AFTER__*/
}

.forum__row {
	padding: 0.6em 0.8em;
	/*background-color: rgb(245, 245, 245) !important;*/
	background: transparent !important;
	margin: 0;
	border-radius: 0;
	margin: 0;
	border: none;
	/*border-bottom: 1px solid rgba(16, 16, 16, 0.5);*/
}

.forum__row:hover {
	background: linear-gradient(to right, rgba(0, 0, 0, 0.05), rgba(0, 0, 0, 0.0)) !important;
	border-radius: 5px;
}

.forum__mod {
	background: transparent;
	font-size: 80%;
}

.forum__mod:before {
	content: "【 "
}

.forum__mod:after {
	content: " 】"
}

.tag,
.thread__body__tags,
.row__counts,
.query-tags:before {
	display: none
}

.create-thread {
	padding: 0.5em 1em !important;
	background: none !important;
	margin: 0;
	border: none !important;
}

.forum__row time-ago {
	margin-right: 38px
}

.thread__user span {
	color: rgb(240, 240, 240)
}

.thread__title {
	color: rgb(255, 255, 255)
}

.thread__body img, markdown img {
	border-radius: 5px;
	box-shadow: 0px 0px 8px rgba(0, 0, 0, 1);
}

.info {
	display: none
}

.user_stats {

}

.user_stats h3 {
	text-align: center
}

.reply,
.reverse-btn,
.char-header,
.studio_header,
.forum__search input,
.search input {
	background: transparent !important;
	border: none !important;
	color: rgb(240, 240, 240) !important;
}

.banner_nav {
	width: auto
}

.banner_nav a {
	line-height: 205%
}

.follow-content {
	text-align: left
}

.row__avatar,
.follow__avatar {
	padding: 0;
	margin: 0;
	border-radius: 100% !important;
	transform: scale(0.9);
}

.row__avatar:hover,
.follow__avatar:hover {
	transform: scale(1.0);
	box-shadow: 0px 0px 16px rgba(0, 0, 0, 0.6);
}

.row__avatar {
	transform: scale(1.0)
}

.row__avatar:hover {
	transform: scale(1.1)
}

.countdown {
	color: rgba(255, 255, 255, 0.5)
}

.char__title,
.rel__type,
.user_stats div,
.data__row span:first-of-type,
.data__row div span:first-of-type,
.row__new {
	color: rgb(95, 177, 255) !important
}

.char__col > a:hover {
	text-shadow: 0 0 8px rgba(95, 177, 255, 0.6)
}

.char__role {
	text-shadow: none !important
}

.series_section .charsLink {
	right: 0;
	top: 26px;
}

.char__row {
	width: 50%
}

.thread__title,
.char__col:last-child,
.series__activity div,
.rel--img .label,
.rel--img .label--alternative:after,
.rel--img .label--sequel:after,
.rel--img .label--prequel:after,
.rel--img .label--other:after,
.rel--img .label--summary:after,
.rel--img .label--adaptation:after,
.rel--img .label--side:after,
.rel--img .label--character:after,
.rel--img .label--parent:after,
.rel--img .label--spin:after {
	border: none
}

.markdown_spoiler:before {
	padding: 0.25em 0.5em;
	content: "Spoiler";
	border-radius: 5px;
	background: rgba(255, 255, 255, 0.1);
	color: rgb(240, 240, 240);
}

.markdown_spoiler span {
	background: transparent !important;
	color: rgb(240, 240, 240);
}

.branch {
	padding-right: 0;
	border: none;
	background: rgba(0, 0, 0, 0.04);
	border-radius: 5px;
}

.forum__thread__comments li:first-of-type {
	margin-left: 6px;
}

.forum__thread__comments .comment .comment__user {
	margin-top: 6px;
}

.commentExpand {
	height: calc(100% - 50px);
}

.current {
	color: rgb(255, 255, 255) !important
}

::selection {
	background: rgb(48, 48, 48);
	color: rgb(255, 255, 255);
	text-shadow: 0 0 8px rgba(255, 255, 255, 0.5);
}

input::selection {
	background: rgb(48, 48, 48);
	color: rgb(255, 255, 255);
	text-shadow: 0 0 8px rgba(255, 255, 255, 0.5);
}

.list_wrapper a {
	color: inherit !important;
}
}
