css:
@-moz-document domain(craigslist.org) {
html, body {background-color: #FF00FF;color: #000000;}
html frameset frame#L div.fbod tbody tr td a b {color: white;}
html frameset frame#R div.fbod tbody tr td a b {color: white; background-color:black;}
/* the text-transform:lowercase; makes the ALL CAPS attention whores behave */
html body a {color: #000000;}
div.threadline.normal {color: #000000;}
table.fbod.threads tbody tr td font {color: #000000;}
table.fbod.threads tbody tr td a {color: #000000; text-decoration: none; text-transform:lowercase;}
table.fbod.threads tbody tr td a b:hover {background-color: #7f0000;color: white;}
table.fbod.threads tbody tr td a:visited {color: #000000;}
table.fbod.threads tbody tr td font:after {display:none;}
/* this is the color of YOUR craigslist handle */
table.fbod.threads tbody tr td span.hnd.own {color: #ff0000 !important; text-decoration: underline;}
/* this is the color of the handle of everyone but you */ 
table.fbod.threads tbody tr td span.hnd {color: #ffffff;text-shadow: 1px 1px 3px black, 0 0 1em #666666, 0 0 0.2em #cccccc;}
table.fbod.threads tbody tr td span.hnd a:hover{color: #ffffff;background-color:#666666;font-size: 110%;text-shadow: 1px 1px 3px black, 0 0 1em blue, 0 0 0.2em blue;}
table.fbod.threads em {color: #FF0000;text-shadow: 1px 1px 3px black, 0 0 1em blue, 0 0 0.2em blue;font-size: 110%;} /* removes the negs */
font b {color: #FFD700;text-shadow: 1px 1px 3px black, 0 0 1em blue, 0 0 0.2em blue;font-size: 115%;} /* removes the pluses */
div#titleLine a b {color: #FFFFFF;text-shadow: 1px 1px 3px black;}
div#titleLine span b {color: #FFFFFF;}
a.contextlink{color: #ffc0cb;text-shadow: 1px 1px 3px black}
table.fbod.threads tbody tr td span.hnd.anon {color: #ffffff;}
table.fbod.threads tbody tr td span.hnd.anon span {color: #ffffff;}
table.fbod.threads tbody tr td span.hnd span.S {color: #ffffff;}
/* this above section will make it easier to see handles */
body a.pln {color: #000000;} 
span {color: #FFFFFF;text-shadow: 1px 1px 3px black;}
span.R {color: #FFFFFF;text-shadow: 1px 1px 3px black, 0 0 1em blue, 0 0 5em blue;} /* link and pic */ 
fieldset {background-color: #000000; }
legend {color: #00ff00; background-color: #000000;}
b {font-weight: bold;color: #000000;}
div.index {background-color: #000000;}
span.threadcount{color:#000000}
input[type="submit"] {border:1px solid #00FF00;background-color: #000000;color:#00DD00;}
input[type="checkbox"] {display:none;}
input[type="button"] {color:#ee61cc}
textarea {border:1px solid #00FF00;background-color: #000000;color: #00FF00;}
select, option [type="submit"] {border:1px solid #00FF00;background-color: #000000;color:#00DD00;}
frame#R a.pln {display:none;}
/********************************************************************/
/* this bit will dim all images, silently muting the image spammers */
/*                                                                  */
/*   simply mouse over the images to display them dynamically       */
/*                                                                  */
/********************************************************************/
img {background-color: #00FF00;opacity: 0.05;border:1px dashed #00FF00 !important;} 
img:hover {opacity: 1.0;border:1px solid #00FF00 !important;}
::-webkit-scrollbar {
      width: 15px;
}
::-webkit-scrollbar-track {
      background-color: #ee61cc;
} /* the new scrollbar will have a flat appearance with the set background color */
::-webkit-scrollbar-thumb {
      background-color: #ec50ff;
  		border:1px black solid;
} /* this will style the thumb, ignoring the track */
 
::-webkit-scrollbar-button {
      background-color: #cc0ee3;
  		border:1px black solid;
} /* optionally, you can style the top and the bottom buttons (left and right for horizontal bars) */
 
::-webkit-scrollbar-corner {
      background-color: black;
  border:1px black solid;
}  
div.handleinfo {background-color: #FF0080;}
div.handleinfo table {background-color: #FF0080;}
div.handleinfo table tr {background-color: #FF0080;}
div.handleinfo table tr th {background-color: #FF0080;}
div.reputation {background-color: #FF0080;}
}
