css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {
body {
	font-family: Tahoma, Arial, Helvetica, sans-serif !important;
	font-size: 28px !important;
	font-style: normal !important;
	line-height: 1.33 !important;
	font-weight: normal !important;
	font-variant: normal !important;
	color: #000000 !important;
	background-color: #E1DDD7 /*#c8b8a4*/ !important;
	background-image: none !important;
	background-repeat: no-repeat !important;
	white-space: normal !important;
	height: auto !important; 
	width: 85%!important;
	margin-top: 45px !important;
	margin-bottom: 45px !important;
	margin-left:5% !important;
	margin-right: 7%;
	letter-spacing: 0.03em !important;
	word-spacing: 0.07em !important;
}

	body  * { background-color:  #E1DDD7 !important;
		clear:  both !important;}

h1 {
	font-family:  Verdana, Arial, Helvetica, sans-serif !important;
	font-size: 1.2em !important;
	font-style: normal !important;
	line-height: 1.2 !important;
	font-weight: 900 !important;
	color:#096A40 /*#EBB8D8*/ !important;
	margin:  0 0 10px 0 !important;}
h2 {
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
	font-size: 1.1em !important;
	font-style: normal !important;
	line-height: 1.2 !important;
	font-weight: 800 !important;
	color: #800000 !important;
	margin:  0 0 10px 0 !important;	
	letter-spacing:0.05em !important;}

h3 {
	font-family: Verdana, Arial, Helvetica, sans-serif !important;
	font-size: 1em !important;
	font-style: normal !important;
	line-height: 1.2 !important;
	font-weight: 800 !important;
	color: #0A568F !important;
	margin:  0 0 10px 0 !important;	
	letter-spacing:0.05em !important;}
h4 {
	font-size: 0.95em !important;
	line-height: 1.2 !important;
	font-weight: bold !important;
	color: #402653 !important;
	margin: 0 0 10px 0 !important;	
	letter-spacing:0.05em !important;}
	
img {width: 97% !important;
        height: auto !important; 		}

p, p * { 
     margin: 0 !important;
	 margin-bottom: 16px !important;
	 padding: 2px 0 2px 0 !important;
	 }
	 
a {
	border-bottom-style: solid !important;
	border-bottom-width: thin !important;
	}
.List2 {
	list-style-type: lower-alpha !important;
}
.ProofLabel {
	list-style-position: inside !important;
	list-style-type: upper-roman !important;
}
.MathStrong {
	font-weight: 900 !important;
}

a:link {
	text-decoration: none !important;
	color: #4040FF  !important;
}
a:visited {
	text-decoration: none !important;
	color: #953970 !important;
}
a:hover {
	text-decoration: none !important;
	color: #555555 !important;
}
a:active {
	text-decoration: none !important;
	color: #4040FF !important;
}

.citation {
	display:inline !important;
}
h5 {
	font-family: tahoma !important;
	font-size: 22px !important;
	font-weight: 600 !important;
	color: #009966 !important;
}

div.conceptGroup > h1, div.conceptGroup > h2, div.conceptGroup > h3, div.conceptGroup > h4, div.conceptGroup > h5, div.conceptGroup > h6, div.conceptGroup > p   { display: inline !important;}

.conceptGroup {
	margin-top: 10px !important;
	margin-right: auto !important;
	margin-bottom: 10px !important;
	margin-left: auto !important;
}

 ul > li { list-style-type: disc !important;
   padding-left: 25px !important;
}
ol > li {list-style-type: decimal !important;
  padding-left: 25px !important;
}
.slidePart {margin-top: 25px !important;
		margin-bottom: 50px !important;
		clear: both !important;}
.slide {margin-bottom: 25px !important;}

  
img {max-width: 75% !important; }
.shrink50 {max-width: 50% !important; }
.shrink40 {max-width: 40% !important;}
.shrink30 {max-width: 30% !important;}
.shrink20 {max-width: 20% !important; }
.shrink15 {max-width: 15% !important;}
.floatRight {float: right !important;}
.foatLeft {float: left !important;}
.frame {margin: 0 0 25px 25px !important;
	padding: 2px !important;
	border-style: solid !important;
	border-width: thin !important;} 
 
	#mw-navigation, #mw-page-base{display: none !important;}
  div#content{
		width: 90% !important;
	  margin: 0 !important;

   }

}
