css:
@-moz-document domain("stackoverflow.com") {
@media only screen and (max-width : 1024px) {
  html, body {
    min-width: 0px;
  }
  #header, #content, .topbar .topbar-wrapper {
    width: 975px;
  }
  .footerwrap {
    width: auto;
    box-sizing: border-box;
    overflow-x: hidden;
  }
  #sidebar, .sidebar {
    width: 210px;
  }
  #ad502-rooms {
    width: 210px;
  }
  .user-show-new .user-header .user-about-me {
    width: 440px;
  	height: 190px;
  }
  .card {
    margin-right: 14px;
  }
  .col-sidebar {
    margin-right: 10px;
  }
  .user-page .settings-page {
    width: 700px !important;
  }
  .user-page .col-content {
    width: 745px !important;
  }
  .user-show-new .user-panel-left {
    margin-right: 0px;
  }
}
}
