css:
@-moz-document regexp("http://www.zhihu.com/question/.*/answer/.*") {

div.zu-top {
    display: none;
}

div.zm-tag-editor-labels.zg-clear {
    display: none;
}

div.zm-meta-panel {
    display: none;
}

div.zu-main-sidebar {
    display: none;
}

span.count {
    display: none;
}

button.up {
    display: none;
}

i.icon.vote-arrow {
    display: none;
}

span.label {
    display: none;
}

button.down {
    display: none;
}

div.zh-answers-title {
    display: none;
}

img.zm-list-avatar {
    display: none;
}

div.zm-item-vote-info {
    display: none;
}

div.more-awesome {
    display: none;
}

div.zh-summary.summary.clearfix {
    display: none;
}


a.zm-item-vote-count {
    display: none;
}

div.zg-wrap.clearfix {
    display: none;
}

div.feed-item {
    display: none;
}

div.zg-wrap.zu-main {
    font-family: Microsoft YaHei;
    font-size: 16px;
}

h2.zm-item-title.zm-editable-content a {
    font-family: Microsoft YaHei;
    font-size: 26px;
}

button.up {
    display: none;
}

button.down {
    display: none;
}

a.zm-item-vote-count {
    display: none;
}

.zm-votebar .up, .zm-votebar .down{
	display: none;
}

.zm-item-expanded .zm-votebar, .zh-question-answer-wrapper .zm-votebar, .zh-question-answer-wrapper .zm-item-vote-info, .zm-item-expanded .zm-item-vote-info, .zm-item-expanded .zm-item-meta, .zm-item-expanded a.collapse, .zm-item-expanded .full-content {
display: none; 
}

div.zm-editable-content {
    font-style: normal;
}

div.zm-editable-content.clearfix {
    font-style: normal;
}

h3.zm-item-answer-author-wrap a {
    margin-left: 0px;
    font-size: 16px;
}
}
