css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^http:\/\/(www\.)?cheatengine\.org\/forum.*$"),
  domain("forum.cheatengine.org")

{

  body, td {
    background-color: #222 !important;
  }

  .postbody {
    color: #E4E4E4;
  }
  
  .postdetails {
    color: #E4E4E4;
  }
  
  .maintitle {
    color: #fff;
  }
  
  .gen, .genmed, .gensmall {
    color: #fff;
  }

  .name {
    color: #fff;
  }
  
  .code {
    font-size:10pt;
    color: #B294BB;
  }
  
  .quote {
    color: #969896;
  }
  
  a:link, a:active, a:visited, a:hover {
    color: #4183C4 !important;
  }
  
  .topictitle, h1, h2 {
    color: #fff !important;
  }
  
  .nav {
    color: #c0c0c0 !important;
  }
  
  th {
    color: #fff;
  }
    
  span[style="color:#000000"]{
    color: #aaa !important;
  }
}
