css:
@-moz-document url-prefix("http://idfl.me"), domain("idfl.me"), regexp("http://idfl.me") {
html {
background: #000;
}

.above_body {
background: #464646 none;
margin: 0px 0px 10px 0px;
padding: 10px;
}

.navtabs > li {
border-left: 12px solid #464646;
}

.navtabs ul.floatcontainer {
border-top: solid 12px #464646;
background-color: #2E3539;
height: 34px;
font-size: 13px;
font-family: 'Open Sans',sans-serif;
}

.body_wrapper {
padding: 10px;
background: #464646 none;
}

.formcontrols .blockrow {
background: #464646 none repeat-x center bottom;
border-top: solid 1px #e4e4e4;
padding: 12px;
}

.formcontrols .blockrow .dbtech_shouts .dbtech_vbshout_window .dbtech_vbshout_frame {
background: #464646 none;
}

.dbtech_vbshout_shout.alt1 {
vertical-align: middle;
background-color: #464646;
}

.forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow {
background: #464646 none;
background-image: none;
}

.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
color: #1FA4DD;
background: #464646 none;
background-image: none;
display: block;
width: 100%;
float: left;
min-height: 60px;
position: relative;
border-bottom: 1px solid #e4e4e4;
border-left: 1px solid #e4e4e4;
border-right: 1px solid #e4e4e4;
}

.forumbit_post .foruminfo .forumdata .forumtitle a, .forumbit_nopost .forumbit_nopost .forumrow .forumtitle a {
color: #1FA4DD;
}

.wgo_block .blockbody {
border: 1px solid #e4e4e4;
background: rgb(70, 70, 70) none;
background-image: none;
}

.wgo_block .section {
display: block;
padding: 0 0 12px;
font: 12px;
color: #8C918F;
clear: both;
width: 100%;
}

.wgo_block .time {
color: #8C918F;
}

.shade {
color: #8C918F;
}

.forumbit_post .forumdescription {
display: block;
clear: both;
padding-right: 12px;
color: #8C918F;
margin-bottom: 12px;
}

.navlinks {
background: #464646 none;
border: 1px solid #e4e4e4;
padding: 12px 0;
text-align: center;
color: #8C918F;
font-size: 13px;
margin-top: 12px;
margin-bottom: 12px;
width: 100%;
clear: both;
}

.postbitlegacy .userinfo {
float: left;
position: relative;
width: 176px;
padding: 2px 12px 6px;
font-size: 11px;
display: block;
background: #262626 none;
clear: both;
}

.postbitlegacy .postdetails, .eventbit .eventdetails {
float: left;
width: 100%;
background: #262626 none;
background-image: none;
}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
margin-left: 200px;
border-left: 1px solid #efefef;
background: #464646 none;
background-image: none;
padding-bottom: 1em;
}

.postcontent {
word-wrap: break-word;
color: #C8C8C8;
}

.bbcode_container {
margin: 20px;
margin-top: 5px;
display: block;
color: #4A4646;
}
}
