css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("portablefreeware.com") {
    
    @media (max-width: 1260px) {
    
        .inline.app-links.group li {
            height: 14px !important;
            overflow: hidden;
            box-sizing: border-box;
            -moz-box-sizing: border-box;
        }
        
        /* App links Comments icon */
        .inline.app-links.group .comments {
           width: 30px !important;
           background: url("data:image/png;base64,") no-repeat bottom left !important;
           padding-left: 16px;

        }
        
        /* App links Permalink icon */
        .inline.app-links.group .permalink {
            width: 11px !important;
            background: url("data:image/png;base64,") bottom left no-repeat !important;
        }
        
        /* Hides the text */
        .inline.app-links.group .permalink a { color: transparent !important; }

    }
    

    @media (max-width: 1080px) {
        
        /* App links Screenshot icon */
        .inline.app-links.group .screenshot {
            width: 24px !important;
            background: url("data:image/png;base64,") bottom left no-repeat !important;
        }
        
        /* Hides the GIF screenshot indicator */
        .inline.app-links.group .screenshot .animscreenshot span {
            background: none !important;
        }
    
         /* Hides the text */
        .inline.app-links.group .screenshot a { color: transparent !important; }
    }

}
