/************CSS reset: Make everything have a blue background and remove body padding************/
@-moz-document domain("toodledo.com"), url-prefix("http://localhost/personal/gtd"), url-prefix("http://www.diegomaranan.com/personal/gtd"), domain("ourgroceries.com")  {
    /* Based on http://meyerweb.com/eric/tools/css/reset/ 
    v2.0 | 20110126
    License: none (public domain)
    */
    @font-face {
        font-family: 'Open Sans';
        src: local('Open Sans'), local('OpenSans'), url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
    }

    html, body {
        margin: 0;
        padding: 0;
        border: 0;
        font-size: 100%;
        font: inherit;
        vertical-align: baseline;
        background: rgb(121, 158, 180);
    }
    
    html, body, div, span, applet, object, iframe,
    h1, h2, h3, h4, h5, h6, p, blockquote, p, pre,
    a, abbr, acronym, address, big, cite, code,
    del, div, dfn, em, img, ins, kbd, q, s, samp, 
    small, span, strike, strong, sub, sup, tt, var,
    b, u, i, center,
    dl, dt, dd, ol, ul, li,
    fieldset, form, label, legend,
    table, caption, tbody, tfoot, thead, tr, th, td,
    article, aside, canvas, details, embed, 
    figure, figcaption, footer, header, hgroup, 
    menu, nav, output, ruby, section, summary,
    time, mark, audio, video, html,
    body, div, span, applet, object, iframe,
    h1, h2, h3, h4, h5, h6, p, blockquote, pre,
    a
    {
        font-family: 'Open Sans', 'Trebuchet MS', Verdana, sans-serif !important;
    }
}

