/* Clear background */

@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("kinopoisk.ru") {  

body {background:#d5d5d5 url(http://st.kp.yandex.net/images/noBrandBg.jpg) 50% 0 no-repeat!important;
padding: 1px;
}

.png_block {background: url(http://st.kp.yandex.net/images/noBrandMenuBg.png)!important;top:40px!important} #top_form, #top .menu {top:40px!important} .brand,.branding_flash {display:none!important}

#top {
    margin-bottom: 0px !important; 
}

/* added 19.02.2016 */
div#popup_info {border-top-color: rgb(255, 102, 0) !important;}
