css:
@-moz-document domain("xat.com") { 

body,td,th {
	font-family: Century Gothic;
	color: #ff2a00;
    text-shadow: 3px 3px 8px 2px #000000;
}
body {
	background-color: #000001;
  	font-family: Century Gothic;
  	text-shadow: 3px 3px 8px 2px #000000;
	cursor: url("http://u.cubeupload.com/LeJon/TwZH9R.png"), default;
	BACKGROUND-POSITION: Center;
	BACKGROUND-ATTACHMENT: fixed;
	BACKGROUND-IMAGE: url("http://i.imgur.com/YoeeH3W.png") !important;
  	BACKGROUND-SIZE: 100% 100% !important;
  	BACKGROUND-REPEAT: NO-REPEAT !important;
}

P {
	FONT-SIZE: 10px;
}
A:link {text-decoration: none; color:#00c0ff}
A:visited {text-decoration: none; color:#c600ff}
A:hover {text-decoration: underline; color:#0072ff}
A:active {text-decoration: none; color:#00c0ff}

.navbar-inner {
    background-color: #000001 !important;
  	background-image: none;
    box-shadow: 3px 3px 8px 2px #000001;
    border: 1px solid #ff2a00;
}
  
#hero {
  	background: url("http://i.imgur.com/O7sKkCw.png") !important;
  	background-size: 100% 490px !important;
  	background-repeat: no-repeat !important;
    background-color: #000001 !important;
    box-shadow: 3px 3px 8px 2px #000001;
    border: 1px solid #ff2a00;
}

.btn-primary,.carousel-control {
    background-color: #000001;
  	background-image: none;
}
  
#hero .item p {
  	font-family: Century Gothic;
    font-size: 15px;
    color: #ff2a00;
}

#groups .thumbnail {
  	background-color: #000001 !important;
  	border: 1px solid #ff2a00;
  	box-shadow: 3px 3px 8px 2px #000001;
}

#groups .thumbnail .pic {
  	box-shadow: 3px 3px 8px 2px #000001;
}
#footer {
  	color: ff2a00 !important;
  	background-image: none;
  	background-color: #000001;
    box-shadow: 3px 3px 8px 2px #000001;
    border: 1px solid #ff2a00;
}
iframe{
  	display:none
}
embed{
    box-shadow: 3px 3px 8px 2px #000001;
    border: 1px solid #ff2a00;
}
.foot {
    background-image: none;
    font-size: 10px;
    background-color: black;
}
}
