css:
@-moz-document domain("xat.com") { 

body,td,th {
	font-family: Century Gothic;
	color: #ff7199;
    text-shadow: 3px 3px 8px 2px #000000;
}
body {
	background-color: #720020;
  	font-family: Century Gothic;
  	text-shadow: 3px 3px 8px 2px #000000;
	cursor: url("http://u.cubeupload.com/LeJon/1iyYeJ.png"), default;
	BACKGROUND-POSITION: Center;
	BACKGROUND-ATTACHMENT: fixed;
	BACKGROUND-IMAGE: url("http://i.imgur.com/sNi6ENy.png") !important;
  	BACKGROUND-SIZE: 100% 100% !important;
  	BACKGROUND-REPEAT: NO-REPEAT !important;
}

P {
	FONT-SIZE: 10px;
}
A:link {text-decoration: none; color:#00c0ff}
A:visited {text-decoration: none; color:#c600ff}
A:hover {text-decoration: underline; color:#0072ff}
A:active {text-decoration: none; color:#00c0ff}

.navbar-inner {
    background-color: #720020 !important;
  	background-image: none;
    box-shadow: 3px 3px 8px 2px #720020;
    border: 1px solid #ff7199;
}
  
#hero {
  	background: url("http://i.imgur.com/3jGRPjq.png") !important;
  	background-size: 100% 490px !important;
  	background-repeat: no-repeat !important;
    background-color: #720020 !important;
    box-shadow: 3px 3px 8px 2px #720020;
    border: 1px solid #ff7199;
}

.btn-primary,.carousel-control {
    background-color: #720020;
  	background-image: none;
}
  
#hero .item p {
  	font-family: Century Gothic;
    font-size: 15px;
    color: #ff7199;
}

#groups .thumbnail {
  	background-color: #720020 !important;
  	border: 1px solid #ff7199;
  	box-shadow: 3px 3px 8px 2px #720020;
}

#groups .thumbnail .pic {
  	box-shadow: 3px 3px 8px 2px #720020;
}
#footer {
  	color: ff7199 !important;
  	background-image: none;
  	background-color: #720020;
    box-shadow: 3px 3px 8px 2px #720020;
    border: 1px solid #ff7199;
}
iframe{
  	display:none
}
embed{
    box-shadow: 3px 3px 8px 2px #720020;
    border: 1px solid #ff7199;
}
.foot {
    background-image: none;
    font-size: 10px;
    background-color: black;
}
}
