    width: 100%;
    padding: 7px;
    margin: 0px;
    margin-left: -8px;
    margin-right: -8px;
    border: 1px solid #005D7F;
    color: #707070;
    background : black;
}

div.texta{
    margin: 0px 8px 0px 8px;
}

table.tags_pan td{
    text-align: center;
    border-right: 1px solid #005D7F;
    height: 27px;
    width: 100px;
    padding: 0px;
}
table.tags_pan td a{
    display: block;
    line-height: 27px
}
table.tags_pan td a img{
    vertical-align:middle;
}
table.tags_pan td a:hover{
    background: #005D7F;
}
/* Text
-----------------------------------------------------------------------------*/
.tops
{
    border-radius: 2px;
    background: #001933 no-repeat left;
    border : 1px solid #005D7F;
    padding : 2pt 3pt 3pt 11pt;
    display: block;
    margin-top: 8px;
    margin-bottom: 8px;
}

.tops2
{
    border-radius: 2px 2px 0px 0px;
    background:  #001933 no-repeat left;
    border : 1px solid #005D7F;
    padding : 3pt 3pt 3pt 11pt;
    display: block;
    margin-top: 8px;
}

.spoiler
{
    border : 1px solid #005D7F;
    border-top: 0px;
    padding : 2pt;
    display: block;
    margin-bottom: 8px;
    border-radius: 0px 0px 2px 2px;
}

.quote {
    border: 1px solid #11AAAA;
    color: #11AAAA;
    margin: 8px 0px 8px 0px;
    padding: 3px;
    background-color: #112233;
    display: block;
    border-radius: 2px;
}

.backcode{
    opacity: 0.5;
    border-radius: 2px;
    background-color: #00BBFF;
    border : 1px solid #005D7F;
    font: 13px monospace;
    margin: 8px 0px 8px 0px;
}

.backcode:hover{
  opacity: 1;
}
  
.codeline1, .codeline2{
    margin-left: 0px;
    padding-top: 0px;
    padding-bottom: 0px;
    border-bottom:  1px solid #707070;
    border-left:  1px solid #707070;
    color: #000000;
    padding-left: 5px;
    font-size: 11px;
    font-weight: normal;
}

.linetext{
    padding: 1px 2px 1px 2px;
    display: block;
    font-size: 14px;
}

.linetext div{
    display: inline;
}

.codeline1 span.linetext, .codeline2 span.linetext {
    color: black;
}

.und_code{
    border: 1px dotted #999;
    border-top: 0px;
    font-size: 9pt;
    padding: 1px 3px 1px 3px;
    background: #e0e6e9;
}

