.controls {
    font-weight: normal;
}

.controls a {
    display: inline-block;
    width: 15px;
    height: 17px;
    text-align: center;
    vertical-align: middle;
}

.controls a:hover{
    text-decoration: none;
    cursor: pointer;
}

.presentation {
    border: 1px solid #d9d8d8;
    border-top: 0;
    padding: 4px;
    display: block;
    background: white;
}


td.switch{
    white-space: nowrap;
    border-right: 1px solid #d9d8d8;
    padding: 5px;
}

td.tthead{
    font-weight: bold;
    font-size: 13px;
    width: 100%;
    text-align: left;
    padding: 5px;
}
