css:
@-moz-document url-prefix('https://www.tumblr.com') {

body:not(.mceContentBody) {
	background: url('http://s22.postimg.org/f651j6a9t/7929317256_af87116a9d_h.jpg')
	  center right #000000 repeat fixed !important;
	background-size: cover !important; /*++*/
	  /* zoom the background just enough to cover everything */
}


/*
	l & r columns container
*/
div.l-content.l-content.l-content {
	background: rgba(27,0,45,0.1);
	border-radius: 20px;
	margin: auto;
	padding: 20px 20px;
}

ol#posts li blockquote,
body.mceContentBody blockquote {
	margin-left: 0 !important;
	margin-right: 0 !important;
	padding-left: 10px !important;
	border-width: 4px !important;
	border-color: #000000 !important;
}


/* 
	left column - posts & avatars
*/
DIV.post_avatar, DIV.post_avatar .post_avatar_link,
#left_column, #right_column {
	background-color: transparent;
}


	/*
		editor
	*/
DIV.main_content {color: white}
DIV.main_content textarea,
DIV.main_content input[type="text"] {color: #a1a1a1}
.main_content .drop_zone {background-color: /*rgba(255,255,255,.4)*/rgba(9,29,54,1)}

	/*
		'new post' icons
	*/
DIV.new_post.new_post_buttons {background-color: /*rgba(75, 0, 130, 0.4)*/rgba(9,29,54,.65)}
DIV.new_post.new_post_buttons:not(.editing):hover {background-color: /*#251053*/rgba(9,29,54,1)}
	/* translucent to opaque background on hover */

/*
	Icon code is from "Tumblr 'new post' icons generator" by hideheader
	https://userstyles.org/styles/103211/tumblr-new-post-icons-generator
	License is Creative Commons BY-NC-SA - do not remove this notice.
*/
a.new_post_label > i {
	height: 90px;
}

.new_post_label {
	/*padding: 61px 1px 18px 0 !important;*/
	/*text-transform: lowercase;*/
	/*color: midnightblue !important;*/
}

.new_post_label > I {
	background: url('http://s7.postimg.org/6qegzxo0b/yeah.png') no-repeat;
	color: transparent !important;
	transition: background-position .08s linear !important;
}
#new_post_label_text > I {background-position: 2px -5px}
#new_post_label_photo > I {background-position: -73px -5px}
#new_post_label_quote > I {background-position: -148px -5px}
#new_post_label_link > I {background-position: -223px  -5px}
#new_post_label_chat > I {background-position: -298px -5px}
#new_post_label_audio > I {background-position: -373px -5px}
#new_post_label_video > I {background-position: -448px  -5px}

#new_post_label_text:hover > I {background-position: 2px -9px}
#new_post_label_photo:hover > I {background-position: -73px -9px}
#new_post_label_quote:hover > I {background-position: -148px -9px}
#new_post_label_link:hover > I {background-position: -223px  -9px}
#new_post_label_chat:hover > I {background-position: -298px -9px}
#new_post_label_audio:hover > I {background-position: -373px  -9px}
#new_post_label_video:hover > I {background-position: -448px  -9px}

#new_post_label_text:active > I {background-position: 2px -5px}
#new_post_label_photo:active > I {background-position: -73px -5px}
#new_post_label_quote:active > I {background-position: -148px -5px}
#new_post_label_link:active > I {background-position: -223px -5px}
#new_post_label_chat:active > I {background-position: -298px -5px}
#new_post_label_audio:active > I {background-position: -373px -5px}
#new_post_label_video:active > I {background-position: -448px -5px}
/*
	Icon code is from "Tumblr 'new post' icons generator" by hideheader
	https://userstyles.org/styles/103211/tumblr-new-post-icons-generator
	License is Creative Commons BY-NC-SA - do not remove this notice.
*/


/*
	right column - menus
*/
#right_column .controls_section li:hover:not(.section_header) {background-color: /*#251053*/rgba(9,29,54,1)}
	/* menu items */
#right_column .controls_section.user_list li .follow_list_item_blog:before,
	/* blue fade at the right of each menu item */
#right_column:after {display: none}
	/* blue fade at the bottom of the right column */


/* OLD OLD OLD OLD... */
h1 {
	color: #131313;
	text-shadow: 0 0 3px #000000;
}

.no_posts_found, h1.dashboard_header {
	color: #000000;
}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current,
#right_column .dashboard_nav_item {
	background: none;
}

#right_column .dashboard_nav_item #hide_radar {
	background: #131313;
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo,
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset,
#right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
	background: transparent;
}

#nav .nav_item.active {
	background: rgba(00,0,0,1) !important;
}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel,
ol#posts li.notification,
ol#posts li.notification.alt,
ol#posts li.notification.first_notification,
ol#posts li.post .permalink {
	background-color: rgba(0,0,0,0.5);
}

ol#posts li.notification,
ol#posts li.notification.last_notification {
	border-bottom: 1px solid #000000;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
	background: rgba(0,0,0,0.8);
	border: solid #000000;
}

ol#posts li.post .post_avatar {
	border-bottom: 0px solid #000000;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
	border-top: 1px solid #000000;
	border-bottom: 1px solid #000000;
}

#right_column a#dashboard_switch_blog_menu_current:hover,
#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
	background: none;
}

form.dashboard_options_form {
	background-color: rgba(0,0,0,0.0);
	border-bottom: 1px solid #000000;
	border-radius: 10px;
	color: #000000;
	display: block;
	font-size: 13px;
	margin-bottom: 25px;
	padding: 15px 22px;
}

form.dashboard_options_form .option_container {
	border-top: 1px solid #000000;
}

.no_posts_found, h1.dashboard_header {
	color: #131313;
}
/* ...OLD OLD OLD OLD */


/*
	header ("Tumblbar")
*/
DIV.l-header-container {background-color: /*rgba(75, 0, 130, 0.4)*/rgba(9,29,54,.65)}
DIV.l-header-container:hover {background-color: /*#251053*/rgba(9,29,54,1)}

	/*
		logo
	*/
#logo {
	/*position: absolute;*/
	width: 181px;
	height: 35px;
	margin-top: 8px;
}
#logo > .logo_anchor {
	background-image: url('http://s16.postimg.org/wfp6yx68l/tumblr_blue.png') !important;
	background-size: auto;
	background-position: -3px -7px;
}

	/*
		'compose' button
	*/
.l-header > BUTTON.compose-button {
	background-color: transparent;
	border-color: rgba(255,255,255,.5);
}
.l-header > BUTTON.compose-button:hover,
.l-header > BUTTON.compose-button:active,
.l-header > BUTTON.compose-button:focus {
	color: rgba(255,255,255,1);
	border-color: rgba(255,255,255,1);
}
.l-header > BUTTON.compose-button:active {color: rgba(255,255,255,.5)}


/*
	return to top button
*/
.elevator /*#return_to_top .return_to_top_icon*/ {
	background: url('http://i.imgur.com/49Eej.png') no-repeat transparent!important;
	width: 50px; height: 50px;
}
.elevator:before {display:none}

/* account settings */
.settings_group {
	background-color: /*#17253B*//*rgba(23,37,59,.65)*/rgba(9,29,54,.65);
	color: white;}
#email_hash {filter: invert(0.75)}

}
