css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linux.org.ru") {
*{
       font-family:  DejaVu Sans;

}
    html, body {
    font-size: 1.07em;
    }
.entry-body{
       color: #BABDB6 ;   
       filter: sepia(25%) brightness(90%);
}
    h2 a{
        color: #556B2F !important;
           filter: sepia(30%);
        
    }
.boxlet {
    background-color: rgba(39, 44, 45, 0) !important;
    border: 1px solid #3E3E3E;
    border-radius:  0;
    filter: sepia(50%);
}    
#hd .menu a {
    color: #C90;
    filter: sepia(78%);
}
 a{
     text-decoration:  none !important;
 }
#bd a.tag {
    color: #A67B07;
}
html, body {
    background-color: #272822;
}
#bd .sign a {
    color: #91B46F;
}
a {
    color: #A7A15E;
}
    
    img {
/*   filter: grayscale(0.5); */
      filter: sepia(100%) contrast(200%) brightness(70%);
}
    img:hover{
      filter: none;  
    }
    
    .messages .msg {
    background: none repeat scroll 0% 0% rgba(185, 185, 185, 0.07);
}
    
    .code {
    background-color: #2F2F2F;
        filter: sepia(50%);
}
    
    .stars {
    color: #8AA28C;
    }
    
   .group-label {
    background-color: rgba(200, 125, 5, 0.44);

}
    #bd .btn-default { 
    background-color: rgba(0, 0, 0, 0.38);
}
    input, textarea{
        background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.03);
       border: 1px solid black !important;
    }
    
    #bd .btn {
    border-radius: 0.2em;
    border: none;
    color: #EEEEEC;
    background-image: none; 
    background: rgba(221, 221, 221, 0.14) !important;
}
    .infoblock{
        background-color: rgba(44, 44, 44, 0.91) !important;
         filter: sepia(30%);
    }
    
    
   #wiki-page  #user-menu{
        background-color: transparent !important;
    }
    #wiki-page #tab_menu *{
        color: #AF7DFF;
          filter: sepia(20%);
    }
    #wiki-page #tab_menu .active *{
        color: #F51E6D;
    }
    #wiki-page ul.tab-menu li {
    background-color: transparent;
    }
    #wiki-page #contents{
     background-color: transparent;
    }
    #wiki-page  table, #toc{
        background-color: transparent !important;
    }
    [style^="display: inline-table; width: 75%;"]{
        background-color: transparent !important;
    }
    [style^="display: inline-table; width: 75%;"] *{
        color: #BABDB6 !important;
    }
     div[style^="display: table-cell;"], div[style^="display: table-cell;"] *{
         border-right: none !important;
         color: #AF5F00 !important;
     }
    [style^="display: inline-table; width: 75%;"]:first-child {
        border: 1px solid #555 !important;
    }
    a.externallink:after {
        display: none !important;
    }
    #wiki-page pre {
    background-color: #2F2F2F;
        border: 1px dashed #777;
    filter: sepia(40%);
}
    .editsection *{
        color: #F0E68C !important;
         filter: brightness(80%);
    }
    #contents{
        border: none !important;
    }
    #wiki-navigation{
        left: 15px !important;
    }
    #wiki-content {
    padding: 20px;
       padding-top: 0;
}
    #wiki-page i{
        color: aliceblue;
    }
    #category-index a.edit{
        color: #AB7AFA !important;
    }
    .portlet {
    background: none !important;
    border: none !important;
    }
    #wiki-content hr{
       border-bottom: 1px solid #555753;
    }
    #toc{
        border: none !important;
    }
}
