css:
@-moz-document url("https://api.t411.me/") {
body {
    margin: 50px;
}

form.api input {
    height: 30px;
    letter-spacing: 0.2px;
    font-family: Verdana;
}

form.api input[type=text], #token {
    font-family: 'ubuntu mono', monospace;
    font-size: 1.1em;
    line-height: 1.5em;
    letter-spacing: 0.1em;
    margin: 10 20 0 20;
    width: 65%;
    padding: 10px;
}

form.api pre code {
    padding: 5px;
}

pre {
    padding: 5px;
    padding-bottom: 10px;
    background-color: #d1d1d1;
    width: 70%;
    overflow: auto;
    margin: 20;
}

html body h3 {
    padding-top: 20px;
}

html body h2 {
    padding-top: 50px;
}

html body pre {
    background-color: #b8b8b8;
}

pre code em {
    color: #ff5454;
}

body pre code {
    font-family: 'ubuntu mono', monospace;
    line-height: 1.5em;
    letter-spacing: 0.1em;
}

}
