@namespace url

(http://www.w3.org/1999/xhtml);

@-moz-document domain("rappelz-forum.gpotato.com") {


/* - collapse the banner to see the first post without scrolling */
#rp_main_banner {display:none !important;}


/* = = = do NOT combine the next 5 = = = */
/* - simplify the red navigation menu - the next 5 lines */
#rp_main_header_bg {display:block !important;}
#rp_main_header_bg2 {display:none !important;}
#rp_main_header_btn {display:none !important;}
#rp_main_header_download {display:none !important;}
#rp_main_header_character_pop {display:none !important;}
#rp_main_header_download {display:none !important;}


/* NOTE: Library, Arsenal and Profile & Achievement links will vanish */
/* - hide the left navigation bar */
#rp_contents_left {display:none !important;}

/* = = if unhide left nav-bar then omit the width:100% below  = = */

/* - make forum content opaque background & full width */
#content_wrapper_full {
background:white !important;
width:100% !important; 
}

/* - clean up control panel LH nav-bar links */
#navigation a       {background-color:#bbccff !important;
                     background-image:none !important; }
#navigation a:hover {background-color:#ccddff !important;}


/* = = = THE FOLLOWING ARE NOT RECOMMENDED ... BUT MAY SUIT YOU = = = */

/* hide the orange gPotato menu ... if you really must 
#gpotato_bar {display:none !important;}
*/

/* hide the BG image at top ... if you really must 
#rp_main_top_bg {background:white !important;}
*/

/* hide a bit more of the red navigation menu ... if you really must 
#rp_main_header_bg {display:none !important;}
*/

/* hide the entire red navigation menu ... if you really must 
#rp_main_header {display:none !important;}
*/

/* - hide the fixed width, red RAPPELZ FORUMS top nav-bar BG image ... try it ... I use it! */
#forum_nav{background-image:none !important;}


/* - hide the footer ... if your really must! 
#rp_footer {display:none !important;}
*/

/* NOTE: forum user's should disable sig via the user control panel */
/* - hide signatures if you browse the forum without logging in 
.signature {display:none !important;}
*/



