body>a:last-child {
    display: block !important;

    margin-left: auto !important;
    margin-right: auto !important;  
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  
    padding: 10px 0px 10px 0px !important;
    text-align: center !important;
}

.updated
{
	color: #4D7599 !important;
}
.new
{
	color: #4D7599 !important;
    background: none !important;
}

#new{ /*Dit is niet zichtbaar maar nodig om de ::before een structuur te geven.*/ 
	display: block !important;
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;
    margin-top: 1em;
    visibility: hidden;
}
#new::before{
    content: 'Nieuwe posts';
  
    display: block !important;
    width: 100% !important;
    margin-left: auto !important;
    margin-right: auto !important;
  	margin-bottom: 0px !important;
    visibility: visible;
    
    border: 1px solid black !important;
    border-bottom-color: #375576 !important;
    border-top-left-radius: 5px !important;
    border-top-right-radius: 5px !important;
  
    padding: 6px 0px 6px 0px !important;
    text-align: center !important;
   
    background-color: #b6dbff !important;  
    /*Op bepaalde browsers zal dit helaas niet werken:*/
    background-image: linear-gradient(#4D7599, #9FBDD7) !important;
  
	color: white !important;
}
#new:hover {text-decoration: none !important;}
body
{
	background-color: #ecedf3 !important;
}

a
{
	text-decoration: none !important;
    color: #111 !important;
    display: inline-block !important;
    height: 100% !important;
}
a:only-of-type
{
	width: 90% !important;
}
a:first-child
{
	width: 90% !important;
}
a:nth-child(4)
{
	width: 90% !important;
}
a:hover
{
	text-decoration: underline !important;
}
.titlebg a
{
	color: white !important;
    background: none !important;
}

#message {
	width: 90% !important;
	margin-left: 4% !important;
}
}

