css:
@-moz-document domain("eddb.io") {
/* Elite Orange: #FF4D00 */

html.site-eddb, body.site-eddb, .wrap { 
  background-color: #000;
}
html, body { 
  color:#fff; 
}
.page-header {
  color: #FF4D00;
  border: none;
}
.page-header h1 i, h1 .prefix, .breadcrumb > .active { 
  color: #fff;
}
.page-header h1, .page-header .h1 { 
  border: none;      
}

a {
  color: #FF7E00;
}

/* more like elite interface */
.panel { 
  background-color: #000;
  border: 1px solid #FF4D00;
}
.panel-eddb>.panel-heading,
.panel-subheading  { 
  background-color: #000; 
  border-bottom: 1px solid #FF4D00; 
  border-top: 2px solid #FF4D00;
  text-transform: uppercase;      
}

/* Table */
.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td { border: none; }
.table-bordered {
  border: 1px solid #ef7b25;
}


.table-wrap { overflow-x: hidden; }
.table .table { background-color: #000; }
table.dataTable thead th,
.panel table.panel-table .route-result-item td, 
.FixedHeader_Header table.panel-table .route-result-item td {
 background-color: #000 !important;          
}
th.sorting, 
table.dataTable thead th.sorting:after {
  color: #FF4D00; 
  text-transform: uppercase;
}
table.dataTable thead th.sorting_asc:after { 
  color: #fff;
}
/* Highlight selected column */
table.dataTable thead th.sorting_asc,
table.dataTable thead th.sorting_desc {
  background-color:#FF7E00 !important; 
  color: #fff; 
  text-transform: uppercase;
}
table thead th, table.dataTable thead th,
table thead td, table.dataTable thead td {
  border-left: 1px solid #FF4D00 !important;
  border-bottom: 1px solid #FF4D00 !important;
}
.panel table.panel-table .route-result-item .result-total, 
.FixedHeader_Header table.panel-table .route-result-item .result-total {
  color: #0BACF2;
}
table.dataTable tbody tr {
  background-color:#150900 !important;
}
.panel-eddb .table>tbody>tr>td {
 border-top: 3px solid #000 !important;
 border-bottom: 3px solid #000 !important;
 padding: 15px 10px;
 border-right: 2px solid #411400;
}
.table-striped > tbody > tr:nth-of-type(odd) {
  background-color:#150900;      
}
table.dataTable tbody tr.group {
  text-transform: uppercase;
  
}  
table.dataTable tbody tr.group strong {
  font-weight: normal;
}

ul.pagination {
  
}
.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
  color: #FF7E00;  
  background-color: #000;
  border-color: #FF7E00;
}     
.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
  color: #fff;
  background-color: #FF7E00;
  border-color: #FF7E00;
}
.pagination > li > a, .pagination > li > span {
  padding: 6px 12px;
  margin-left: -1px;
  color: #FF7E00;
  background-color: #000;
  border: 1px solid #FF7E00;
}
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
 color: #fff;
 background-color: #341600;
 border-color: #FF7E00;
}
      
.footer { 
  background-color: #000;
  border-top: 1px solid #FF4D00;
          
}


/** Panel is Loading **/
.panel-loading-drop.panel-is-loading:before {
	opacity: 0.4;
	top: -3px;
	bottom: 0;
	right: 0;
	left: 0;
}
.panel-loading-drop:before {
	content: "";
	opacity: 0;
	z-index: 1000;
	-webkit-transition-delay: 0s;
	transition-delay: 0s;
	-webkit-transition-duration: 100ms;
	transition-duration: 100ms;
	-webkit-transition-property: opacity;
	transition-property: opacity;
	background-color: #FF4D00;
	border-radius: 4px;
}
}

@-moz-document domain("ross.eddb.io") {
.eddb-link img, span.eddb-link i.fa-building-o { display: none; }
}
