css:
@-moz-document url-prefix("http://pikabu.ru/") {
/*////////////////*/
/* Верхняя панель */
/*////////////////*/

div[style="width: 100%; background: url('http://cs.pikabu.ru/images/header_bg13.gif') #f0f0f0 repeat-x bottom;"] {
    background: #333 !important;
    color: #ccc !important;
} /* сама панель */

.header_links a {
    color: #ccc !important;
} /* ссылки справа*/

#logo {
    border: 0 !important;
    opacity: 0.8;
} /* логотип */

.noactive a {
    background: #444 !important;
    color: white !important;
} /* неактивная вкладка (свежее, лучшее, горячее) */

.active a, .active2 a {
    background: #000 !important;
    border: 0 !important;
} /* активная вкладка */

/*////////////*/
/* Содержимое */
/*////////////*/

#wrap, body, html {
    background: #444 !important;
    color: #ccc !important;
} /* фон */

.pg_click {
    background: none !important;
    color: #ccc  !important;
} /* заголовок поста */

.pg_click a {
    color: #ccc !important;
}

.b-rating__count {
    color: #bbb !important;
} /* рейтинг поста */

#hide_news_counter, #viewmode_bar  {
    background: #999 !important;
    color: #fff !important;
} /* Скрыто просмотренных */

.pc-line-sec, .active-item-sh-time, .aside-panel-r, .calendar-inline-l input {
    background: #777 !important;
    color: #ccc !important;
} /* Скрыто просмотренных, часть верхняя */

/*//////*/
/* Пост */
/*//////*/

.short {
    color: #bbb !important;
} /* Описание */

.b-story__header-additional span {
    color: #bbb !important;
} /* Пришло от */

.tag {
    background: #222;
    border: 1px solid #000;
    color: #ccc;
    text-shadow: none;
} /* Теги */

.tag:hover {
    background: none !important;
}

.slide_up_container {
    background: none !important;
    color: #bbb !important;
} /* Нижняя панель поста */

.story_head noindex span {
    color: #bbb !important;
} /* Сервер поста */

div.story_head a, .b-story__link, .b-story__link_visited_yes, .story_link, .comm-story-title a {
    color: #ffc !important;
} /* Заголовок */

div.story_head a h1, .b-story__link h1, .b-story__link_visited_yes h1, .story_link h1, .comm-story-title a h1 {
    color: #ffc !important;
} /* Заголовок */

div.story_head a:visited {
    color: #ccc !important;
} /* Заголовок, посещенный */

.text_story_div {
    background: #666 !important;
    color: #fff !important;
    border: 1px solid #333 !important;
}/* Текст */

/*///////////////*/
/* Правая панель */
/*///////////////*/

.name {
    background: #444 !important;
    border: 1px solid #000;
    border-bottom: none;
} /* Ваш ник */

.name h4 {
    background: #444 !important;
}

.name h4 a {
    color: #ccc !important;
}

.personals {
    background: #666 !important;
    color: #bbb !important;
    border: 1px solid #000;
    border-top: none;
} /* Меню под ником */

.personals a, .personals ul li span, .personals ul li span a {
    color: #fff !important;
}

a.count {
    color: #000 !important;
} /* Уведомления */

td[style="background: url(http://cs.pikabu.ru/images/menu_elements_top_bottom.png) no-repeat 0 -38px; height: 7px;"] {
    display: none;
} /* Какая-то лишняя херня */

/*////////////////////*/
/* Под правой панелью */
/*////////////////////*/

.addlink_button {
    background: #777;
    padding-top: 5px !important;
} /* Кнопка "Добавить" */

.addlink_button:hover {
    background: #777;
}

.addlink_button img {
    top: 2px !important;
}

.personal .login_f  {
    border: 0 !important;
}

.personal .login_f ~ .login_f  {
    border: 1px solid #555 !important;
}

.rating_bl {
    background: #222 !important;
    color: #bbb !important;
    text-shadow: none;
} /* Комментарий дня, заголовок */

.rating_bl a {
    color: #ccc !important;
    text-shadow: none;
}

.best_comm, .info, .info_c {
    color: #ccc !important;
} /* Комментарий дня */

.best_comm a {
    color: #ccc !important;
}

frame[title="fb:like_box Facebook Social Plugin"] {
    display: none;
} /* Фэйсбук */

.most_comm_post_show a {
    color: #ccc !important;
} /* Ваши обсуждения */

table[style="border: 1px solid #d7d7d7; border-radius: 3px; width: 302px; margin: 15px 0 0 5px;text-align: center"] {

} /* Рамка */

.tag_all a {
    color: #bbb !important;
}

/*/////////////*/
/* Комментарии */
/*/////////////*/

.comment {
    color: #bbb !important;
} /* Ссылки */

.profile_commented {
    border-bottom: none !important;
} /* Разделитель */

.comment_msg, .comment_desc {
    color: #ccc !important;
} /* Комментарий */

.comment_desc {
    background: #333;
    border: 1px solid #222;
    border-radius: 4px;
    padding: 8px;
} /* Тело комментария */

.best_comm .comment_desc {
    border: none;
    padding: 0;
    border-radius: 0;
    background: none;
} /* Тело в комментарии дня */

.comment_msg_new {
    background: #777 !important;
} /* Непрочитанный */

.comment_msg_new .comment_desc {
    color: #ccc !important;
}

.info_c a, .info a {
    color: #ccc !important;
} /* Автор (ник), "Х минут назад" и ссылка на пост */

button.b-comment__show-reply {
    color: #fff !important;
} /* Кнопка ответа*/

.comm-dividing {
    background: #444 !important;
} /* Разделители */

#comments_num *, .duplicate_wrap * {
    color: #bbb !important;
} /* Кол-во комментариев */

.comm_wrap_counter {
    background: none !important;
} /* Хз что, но надо */

/*////////*/
/* Другое */
/*////////*/

a.dotted {
    color: #ccc !important;
} /* Расширенный поиск */

#blk-upcoming a {
    color: #ccc !important;
} /* Ссылки под расширенным поиском */

*[data-twttr-rendered="true"], .fb-like-box, #facebook_page_block {
    display: none !important;
} /* Твиттер, фейсбук */

.b-navigator {
    background: #333;
    border: 1px solid black;
} /* Нагибатор^WНавигатор */

.b-textarea, .b-textarea:focus, .b-comment-reply__submit, .b-comment-reply__submit:focus {
    background: #222;
    outline: none;
    color: white;
    border: 2px solid #555;
    text-shadow: none;
} /* Поле ввода */

#footer_links_td, .footer, .footer_links2, .footer_links {
    background: #222 !important;
    color: white;
} /* Ноги */

#footer_links_td a, .footer a {
    color: white;
}

.add_story_cn, .tr_link {
    color: #bbb !important;
}
}
