@-moz-document domain("cpaelites.com") {
    /*@media (max-width: 360px) {*/

html,
        body { 
            background: #111; 
            margin: 0;
width: device-width;
max-width: 360px;
        }
        body img { 
            max-width: 320px; 
        }
        body #container {
           padding: 0;
        }
        .menu {
            background: none;
        }
        body .menu ul {
            font-size: 8px;
        }
        body .menu > div {
            display: block !important;;
            float: none !important;;
        }
        body table {
            font-size: 10px;
        }
        body td.trow1:nth-child(3),
        body td.trow1:nth-child(4),
        body td.trow1:nth-child(5),
        body td.trow2:nth-child(3),
        body td.trow2:nth-child(4),
        body td.trow2:nth-child(5),
        body td.tcat:nth-child(2),
        body td.tcat:nth-child(3),
        body td.tcat:nth-child(4) {
            display: none;
        }
        body .sidebar {
            display: none;
        }
        body a.button {
            font-size: 10px;
        }
        .post_management_buttons > div,
        .post_buttons > div {
            float: none !important;
            margin: 15px 0;
        }
        div.forums {
            width: 100%;
            float: none;
        }
        body .thead .smalltext,
        body .thead  strong,
        body .thead {
            font-size: 8px;
        }
        body .trow_sep {
            font-size: 11px;
        }
        td.post_avatar img {
            max-width: 100px;
            width: 100px;
            height: auto !important;
        }
        td.post_avatar {
            float: left;
            max-width: 100px;
            width: 100px;
            margin-right: 10px;
        }
        td.post_avatar,
        td.post_author,
        td.post_author_info {
            display: block;
        }
        td.post_author img {
            display: none;
        }
        td.post_author_info {
            width: 100%;
        }
        td.post_author_info img {
            width: 12px !important;
            height: 12px !important;
            float: right;
        }
        #content form[name="input"] .tborder tr > td:first-of-type {
            display: none;
        }
        .messageEditor {
            max-width: 310px !important;
            width: 310px !important;
        }
        #userpanel .flyouts .flyout {
            left: 0;
            max-width: 300px;
        }
    /*}*/
}
