@-moz-document domain("twitch.tv"), domain("twitchtv.com") {
div.title > span.real,
div.title > span.over{
    background-color:rgb(16,16,16)!important;
}
    
div.title > span.over:hover,    
div.title > span.real:hover{
    background-color:rgb(16,16,16)!important;
    color:rgb(255,255,255)!important;
}


div.chat-header.chat-header {
    box-shadow: 0px -2px 0px rgba(0, 0, 0, 0.04) inset!important;
    background-color: rgb(16,16,16)!important;
    border-bottom: 1px solid rgb(16,16,16)!important;
}
    
    
.ember-chat .chat-menu-button-container span.back{
    background: url('http://herpderp.com/twitch/chat_glyphs.png') no-repeat scroll 0px -45px transparent!important;
}
    
.ember-chat .chat-menu-button-container span.invite{
    background: url('http://herpderp.com/twitch/chat_glyphs.png') no-repeat scroll 0px -65px transparent!important;
}
    
div.chat-header.main-header > .chat-menu-button-container.left,
div.chat-header.main-header > .chat-menu-button-container.right,
div.chat-header.all-rooms-header > button.chat-menu-button-container.right{
    border:none!important;
}
    
div.chat-header.main-header > .chat-menu-button-container.left:hover,
div.chat-header.main-header > .chat-menu-button-container.right:hover,
div.chat-header.all-rooms-header > button.chat-menu-button-container.right:hover{
    background-color:rgb(33,33,33)!important;
}
    
div.chat-header.main-header > p.room-title,
.ember-chat .chat-header.all-rooms-header{
    color:rgb(216,216,216)!important;
}
    
.ember-chat .chat-menu .list-header{
    color:rgb(200,200,200)!important;
    background-color:rgb(25,25,25)!important;
    border:none!important;
    
}

.ember-chat .chat-room-list .room{
    color:rgb(200,200,200)!important;
    background-color:rgb(16,16,16)!important;
    border:none!important;
}

.ember-chat .chat-menu.chat-room-list .no-content{
    color:rgb(200,200,200)!important;
}
    
div.chat-header > button.chat-menu-button-container.right > span.create{
    background: url('http://www.twitch.tv/images/xarth/g/g18_close-FFFFFF80.png') no-repeat scroll 0% 0% transparent!important;
}

.ember-chat .chat-room-list.masked .mask{
    background: none repeat scroll 0% 0% rgb(16,16,16)!important;
    opacity:0.90!important;
}
    
div.chat-create-room.top-dropdown,
div.chat-invite-user.top-dropdown{
    background: none repeat scroll 0% 0% rgb(25,25,25)!important;
    border-bottom:#000!important;
}
    


.js-chat-hidden-overlay{
    background-color:rgb(16,16,16)!important;    
}
    
.js-chat-hidden-overlay > .popped-out-text{
    color:rgb(160,160,160)!important;
}


.ember-chat .chat-messages{bottom:138px!important;}

div#chat > div > div{
    background: none repeat scroll 0% 0% rgb(25,25,25)!important;
    background-color:rgb(25,25,25)!important;
    margin:0px!important;
}


div.scroll.chat-messages{
    margin-top:-6px!important;
    margin-bottom:-6px!important;
}



.ember-chat .chat-interface .chat-buttons-container .chat-option-buttons button.settings{
    background-position: 2px 3px!important;
}
.ember-chat .chat-interface .chat-buttons-container .chat-option-buttons button.viewers{
    background-position: 2px -21px!important;
}
    
.normal_button, .large_normal_button, .dropdown, .dropdown_static, .dropdown_glyph {
    background-color: #0A0A0A!important;
    border-width: 1px!important;
    border-style: solid!important;
    border-color: rgba(0, 0, 0, 0.2) rgba(0, 0, 0, 0.3) rgba(0, 0, 0, 0.35)!important;
    box-shadow: 0px 1px 0px #000 inset, 0px 1px 0px rgba(0, 0, 0, 0.07)!important;
} 

.button-simple.light.tooltip{
background: none repeat scroll 0% 0% #282828;
}

.ember-chat .chat-interface .chat-buttons-container .chat-option-buttons button {
    width: 25px;
    height: 25px;
    background-image: url('http://herpderp.com/twitch/button_glyphs.png')!important;
    background-repeat: no-repeat;
}

.ember-chat .chat-interface .chat-buttons-container .chat-option-buttons button {
    width: 25px;
    height: 25px;
    background-image: url('http://herpderp.com/twitch/button_glyphs.png')!important;
    background-repeat: no-repeat;
}

.ember-chat .chat-messages .chat-line.admin .message{
    color:#ccc!important;
}

.ember-chat .chat-messages .tse-content {
    padding:0px!important;      
}

div.chat-messages, div.chat-line{
    color:rgb(245,245,245)!important;
    padding:0px!important;
    padding-top:0px!important;
}


div.chat-messages .chat-line{
    padding-right:8px!important;
    padding-left:8px!important;
    padding-top:2px!important;
    padding-bottom:2px!important;
    margin-bottom: 0px!important;
}
    
    
div.chat-interface{
    background: none repeat scroll 0% 0% rgb(25,25,25)!important;
    background-color:rgb(25,25,25)!important;
    border-bottom: 1px solid rgb(0, 0, 0)!important;
    border-top: 1px solid rgb(16,16,16)!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05) inset!important;
}

textarea.ember-view{
    border: 1px solid rgb(0, 0, 0)!important;
    background-color: rgb(16,16,16)!important;
    color: rgb(221, 221, 221)!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05), 0px 1px 0px rgba(0, 0, 0, 0.15) inset!important;    
}

div#speak.bottom{
    border:none!important;
}

div#speak_cover{
    display:none!important;
}

textarea#chat_text_input.text{
