    background-color: rgb(16,16,16)!important;
    color: rgb(221, 221, 221)!important;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.05), 0px 1px 0px rgba(0, 0, 0, 0.15) inset!important;
}


.ember-chat .mod-icons a:hover{
    background-color:#202020!important;
}

    A[class="ban"] {margin-right:2px!important;padding-top:2px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://www.herpderp.com/twitch/ban.png') !important;}
    IMG[src*="g18_ban-00000080"] {visibility:hidden!important;}
    A[class="unban"] {margin-right:2px!important;padding-top:2px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://www.herpderp.com/twitch/unban.png') !important;}
    IMG[src*="g18_unban-00000080"] {visibility:hidden!important;}
