css:
@-moz-document regexp("https://volafile\.io.*|https://volafile\.org.*") {
  /*
  color index. the use areas are not complete for reasons
  
  almost black: #272822
    used for chat background color
  
  ligther black: #2d2e2b
    used for chat_hbar and chat left border
  
  even lighter black: #484848
    used for filter reminder and .chat_room_url. this should grab your attention.
  */
  #chat_messages, .header_row, #files_frame, html {
    background-color: #272822 !important;
  }
  
  /*
    Some border on the right/left side of the chat.
  */
  #chat_frame:after {
    border-right: none !important;
  }
  
  #chat_messages_frame:before {
    border-left: none !important;
  }
  
  /*
    Also some border on the top headers
  */
  
  #header_row2:after {
    border-top: none !important;
    border-bottom: none !important;
  }
  
  .ui_frame_body_bar, .dropdown_list {
    border-color: #2d2e2b !important;
  }
  
  #chat_messages_frame {
    border: none !important;
  }
  
  #chat_input {
    background-color: #272822 !important;
    border-color: #2d2e2b !important;
    color: #c7c7c7 !important;
    font-size: inherit !important;
  }
  
  #chat_input[data-old-value=""] {
    color: #7e7e7e !important;
  }
  
  #chat_input:focus {
    color: #c7c7c7 !important;
  }
  
  #chat_hbar, #chat_input_container, #chat_hbar_container {
    background-color: #2d2e2b !important;
    border-color: #2d2e2b !important;
    border-top: none !important;
  }
  
  #chat_name {
    background-color: #272822 !important;
    border-color: #2d2e2b !important;
    font-size: 12px !important;
  }
  
  #chat_name_container {
    background-color: #272822 !important;
    border-color: #2d2e2b !important;
  }
  
  #name_container {
    background-color: #2d2e2b !important;
    border: none !important;
  }
  
  #chat_frame, .header_row, .header_row_element, #files_frame {
    border: none !important;
  }
  
  #home_button {
    background-color: #2d2e2b !important;
    /* border-color: #484848 !important; */
    border: none !important;
  }
  
  #admin_button {
    border-left: none !important;
    background-color: #2d2e2b !important;
  }
  
  #admin_button_icon {
    color: rgba(255, 255, 255, 0.82) !important;
  }
  
  #chat_header {
    border: none !important;
  }
  
  #files_header {
    background-color: #272822 !important;
    border: none !important;
  }
  
  #search_input, input[type="number"], #filter_input {
    background-color: #2d2e2b !important;
    color: #C7C7C7 !important;
  }
  
  /* Top bar filters enabled */
  #toggles .enabled .toggle_icon, #radio_playback_controls .enabled .radio_icon, .music_playing {
    color: #ae81ff !important;
  }
  
  /* Top bar filters disabled */
  #toggles .toggle_icon, #radio_playback_controls .radio_icon {
    color: #888888 !important;
  }
  
  .button {
    background-color: #49483e !important;
    color: #dfdfdf !important;
  }
  
  #gallery_close, #gallery_download {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
  }
  
  #gallery_image_row_container { 
    top: 3em !important; 
    bottom: 3em !important;
    left: 3em !important; 
    right: 3em !important;
  }
  
  #gallery_close_icon {
    display: none !important;
  }
  
  #filter_reminder, #file_notifier {
    background-color: #484848 !important;
    border: none !important;
  }
  
  #filter_reminder:hover, #file_notifier:hover {
    background-color: #545454 !important;
  }
  
  .file_tag {
    background-color: #3b3a32 !important;
  }
  
  .chat_room_url, .scroller-slider, #rename_input {
    background-color: #484848 !important;
  }
  
  #rename_input {
    color: #dfdfdf !important;
  }
  
  td textarea, td input{
    color: #dfdfdf !important;
  }
  
  .ui_frame {
    background-color: #272822 !important;
    border-color: #2d2e2b !important;
  }
  
  .ui_frame_table td {
    border-color: #484848 !important;
  }
  
  .ui_frame_table input, .ui_frame_table textarea {
    background-color: #484848 !important;
  }
  
  #header_row1 .header_row_element:hover {
    background-color: #484848 !important;
  }
  
  .header_row_element:after {
    border-color: #2d2e2b !important;
  }
  
  #toggles > .toggle:hover {
    border-color: #484848 !important;
    background-color: #484848 !important;
  }
  
  .dropdown_item {
    background-color: #272822 !important;
  }
  
  .dropdown_item:hover {
    background-color: #484848 !important;
  }
  
  .file_uploading, .file_queued {
    background-color: #3a4040 !important;
    transform: none !important;
  }
  /*
  .file_uploading {
    background-color: pink !important;
  }
  */
  
  /*
  #files_frame {
    border-color: #2d2e2b !important;
  }
  */
  
  .header_row_element_disabled {
    background-color: #2d2e2b !important;
    color: #484848 !important;
  }
  
  #show_search_ui:hover {
    background-color: #484848 !important;
  }
  
  #radio_control_bar {
    background-color: #272822 !important;
  }
  
  #radio_container {
    border-color: pink !important;
    background-color: #272822 !important;
  }
  
  #radio_current {
    background-color: #2d2e2b !important;
    /*border-color: #484848 !important;*/
    border: none !important;
  }
  
  #radio_current_time {
    border-color: #484848 !important;
  }
  
  #radio_progress {
    background-color: #272822 !important;
  }
  
  #radio_load_progress {
    background-color: #2d2e2b !important;
  }
  
  #radio_play_progress {
    background-color: #484848 !important;
  }
  
  .ui_frame_table select {
    background-color: #484848 !important;
  }
  
  .chat_file {
    background-color: #2d2e2b !important;
    border-color: #484848 !important;
  }
  
  .chat_file:hover {
    background-color: #484848 !important;
  }
  
  .file_selected {
    background-color: #484848 !important;
  }
  
  #chat_messages .chat_message .username { /* Unregistered user */
	  /*color: #dfdfdf !important;*/
    color: rgba(255, 255, 255, 0.84);
  }
  
  #chat_messages .chat_message.user .username { /* Registered user */
    color: #ae81ff !important;
  }
  
  #chat_messages .chat_message.donator .username { /* Donators */
  	color: #c3a2ff !important;
  }
  
  #chat_messages .chat_message.staff .username, #chat_messages .chat_message.admin .username { /* Notifications for mods. Like Log and Report. */
    color: #f92672 !important;
  }
  
  #chat_messages .chat_message.staff.user .username { /* Staff members */
    color: #f36095 !important;
  }
  
  #chat_messages .chat_message.staff.user a.username:before { /* Staff members */
    content: " (Fag) ";
  }
  
  #chat_messages .chat_message.admin.user .username { /* Moderators */
    color: #f92672 !important;
  }
  
  #chat_messages .chat_message.admin.user a.username:before { /* Staff members */
    content: unset;
  }
  
  .chat_message {
    border-left: 0.4em solid #2d2e2b !important;
    transition: right 1000ms linear;
  }
  
  #overlay {
    background-color: #2d2e2b !important;
  }
  
  /* #B891FF donator color */
  
  #file_list.uneven .filelist_file:nth-child(2n+1) { /* First color. */
  	background-color: #272822;
  }
  
  /* First color. */
  /*
  #file_list.uneven .filelist_file:nth-child(2n+1):hover { 
  	border-left-color: #272822;
  }
  */
  #file_list.even .filelist_file:nth-child(2n) { /* Second color. */
  	background-color: #2d2e2b;
  }
  
  #file_list.even .filelist_file:nth-child(2n-1){ /* First color. */
	  background-color: #272822;	
  }

  #file_list.uneven .filelist_file:nth-child(2n+2){ /* Second color */
  	background-color: #2d2e2b;
  }
  
   /* Second color */
  /*
  #file_list.uneven .filelist_file:nth-child(2n+2):hover {
  	border-left-color: #2d2e2b;
  }
  */
  
  
  .filelist_file:hover {
    border-left-color: #484848 !important;
  }
  
  .file_left_part {
    pointer-events:none;
  } 
  
  div.filelist_file > a > span { 
    pointer-events:auto!important; 
  }
  
  .highlight {
    background-color: #2d2e2b !important;
    /* border-left: 0.4em solid #fd971f !important; */
    border-left: 0.4em solid #75715e !important;
  }
  
  #radio_volume_bar {
    background-color: #2d2e2b !important;
  }
  
  #radio_volume_slider {
    background-color: #484848 !important;
  }
  
  /* MAIN MENU */
  
  #main_learn_more, .light.button {
    color: #3a4040 !important;
  }
  
  #top_navigation, .top_nav_item {
    background-color: #2d2e2b !important;
    border-color: #484848 !important;
  }
  
  .main hr {
    border-color: #484848 !important;
  }
  
  #main_small_links {
    background-color: #2d2e2b !important;
  }
  
  /* USER TOP LIST */
  
  th {
    background-color: #2d2e2b !important;
    border-color: #484848 !important;
  }
  
  tbody td {
    border-color: #484848 !important;
  }
  
  .file_status::before {
    border: none !important;
    margin: 0 !important; 
  }
  
  /* Shows the ETA for uploads all the time. */
  .file_right_part .hover_show {
    display:inline !important;
  }

  .file_right_part .hover_show:after {
      content: ' - ';
  }

  .file_right_part:hover .hover_hide {
      display:inline !important;
  }
  
  .gallery_area_right, .gallery_area_left {
    border: none!important;
  }
  
  /* Fixes the gay bullshit where messages and highlighted messages are un-even */
  .chat_message {
    margin-left: 0!important;
  }
  
  .chat_message:last-child {
    margin-bottom: unset!important;
    padding-bottom: 0.2em!important;
    border-left: 0.4em solid #2d2e2b !important;
  }
  
  /* Remove the dumb shadow */

  #chat_hbar_container {
    -webkit-box-shadow: unset!important;
    -moz-box-shadow: unset!important;
    box-shadow: unset!important;
    padding-top: unset!important;
  }
  
  .ui_frame_tooltip.has_thumb .ui_frame_content { 
    border-left: solid 1px #484848!important; 
  }
  
  .chat_text, .file_name, #room_name, .dropdown_options_text, .dropdown_option_text, .chat_file, .file_right_part, #uploadButton > span, .toggle_text, #filter_reminder > *, #filter_reminder, #room_name_angle_down {
    color: rgba(245, 245, 245, 0.95);
  }
  
  #room_settings, #room_timeouts {
    color: rgba(245, 245, 245, 0.95) !important;
  }
    
  /*
    Fixing the wrong colour behind the X that clears searches
  */
  
  #room_search {
    background-color: #272822 !important;
  }
  
  .ui_frame_contextmenu_button > span {
    color: rgba(255, 255, 255, 0.82) !important;
  }
  
  
  
  
  
}
