css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tieba.baidu.com") {
  a, a:visited { color: #CCC }
  
  .wrap1, .skin_normal .wrap1 { background-color: #2D2D2D; color: #CCC }
  .skin_normal .wrap2 { background: transparent }
  .content { background: transparent; border: 0 }
  
  .page-container .content-sec { background: transparent }
  
  .u-f-t .title { color: #CCC }
  .day_rcmd .class_title, .good_rcmd .class_title { color: #CCC }
  .spage_liveshow_slide .slide_outer_wrap .title { color: #CCC }
  .n_name, .n_name:hover { color: #CCC }
  .n_txt { color: #CCC }
  .new_list .title, .n_name { color: #CCC }
  .media_list a, .media_list a:hover { color: #CCC }
  .notice_list a, .notice_list a:hover { color: #CCC }
  .platform_item_item .h6 { color: #CCC }
  
  .card_top_wrap { background: transparent }
  .card_title_fname, .card_title_fname:visited { color: #FFF }
  .card_slogan { color: #CCC }
  
  .forum_content { background: transparent }
  .threadlist_bright li:hover { background-color: #4C4C4C }
  .threadlist_bright li.thread_top_list_folder, .threadlist_bright li.thread_top_list_folder:hover { background-color: #4C4C4C }
  .threadlist_bright .threadlist_title a, .threadlist_bright .threadlist_title a:hover, .threadlist_bright .threadlist_title a:visited { color: #CCC }
  .threadlist_bright .threadlist_abs { color: #CCC }
  .threadlist_bright .threadlist_title a:visited { color: #666}
  
  .region_bright .region_title { color: #FFF }
  .threadListGroupCnt_bright { background: transparent }
  .threadListGroupCnt_bright .listDescCnt { color: #CCC }
  
  .pb_content { background: transparent }
  .left_section { background: transparent }
  .core_title_theme_bright { background: #2D2D2D }
  .core_title h1 { color: #CCC }
  
  .l_post_bright { background: transparent }
  .l_post_bright .d_post_content_main { background: transparent }
  
  .l_post_bright .core_reply_wrapper { background: transparent }
  .lzl_content_main { color: #CCC }
  
  .edui-editor-body .edui-body-container { background: transparent }
  .lzl_editor_container div.edui-editor-body { background: transparent }
  
  #main_wrapper .ibody { background: transparent; border: 0 }
  .feed_hover { background: #4C4C4C }
  .feed_from a, .feed_from a:link { color: #CCC }
  
  .banner_wangmeng_theme { display: none }
  
  /* 回复 */
  .pb_footer, forum_foot { background: transparent }
  .edui-editor-body { background: #2D2D2D }
  
  /* 去除广告贴 */
  div[data-daid], li[data-daid] { display: none !important; }
  
  /* 去掉贴吧首页朵朵奇吧 */
  .forum_recommend { display: none; }
  /* 去除贴吧首页直播间 */
  #spage_liveshow_slide { display: none; }
}
