@-moz-document regexp("http\\:\\/\\/www\\.weerwolvenvanwakkerdam\\.nl\\/forum\\/index\\.php.*wap2.*") {
.catbg {
	border: 1px solid #ADAED7 !important;
    border-bottom-color: #404282 !important;
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;
  	margin-bottom: 0px !important;
  
    padding: 6px 0px 6px 0px !important;
    text-align: center !important;
    
    background-color: #6F71BC !important;
  
    color: white !important;
}
  
.titlebg {
    border: 1px solid #ADAED7 !important;
    border-bottom-color: #404282 !important;
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;   
    margin-bottom: 0px !important;
  
    padding: 6px 0px 6px 0px !important;
    text-align: center !important;
      
	background-color: #6F71BC !important;  
  
	color: white !important;
}
  
.windowbg {
	border: 1px solid #404282 !important;
    border-top-width: 0px !important;
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;  
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  
    padding: 10px 0px 10px 0px !important;
  
  	background-color: #9899CE !important;
}
  
.windowbg2 {
	border: 1px solid #404282 !important;
    border-top-width: 0px !important;
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;  
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  
    padding: 10px 0px 10px 0px !important;
  
  	background-color: #ADAED7 !important;
}

body>a:last-child {
    display: block !important;
    
    border: 1px solid #404282 !important;
    border-top-width: 0px !important;
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;  
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  
    padding: 10px 0px 10px 0px !important;
  
    background-color: #ADAED7 !important;
}

.updated
{
	color: #DCDCDC !important;
}
.new
{
	color: #DCDCDC !important;
    background: #6F71BC !important;
}

#new{ /*Dit is niet zichtbaar maar nodig om de ::before een structuur te geven.*/ 
	display: block !important;
    width: 90% !important;
    margin-left: auto !important;
    margin-right: auto !important;
    margin-top: 1em;
    visibility: hidden;
}
#new::before{
    content: 'Nieuwe posts';
  
    display: block !important;
    width: 100% !important;
    margin-left: auto !important;
    margin-right: auto !important;
  	margin-bottom: 0px !important;
    visibility: visible;
    
    border: 1px solid #ADAED7 !important;
    border-bottom-color: #404282 !important;
  
    padding: 6px 0px 6px 0px !important;
    text-align: center !important;
   
    background-color: #6F71BC !important;  
  
	color: white !important;
}
#new:hover {
    text-decoration: none !important;
}
body
{
	background-color: #212143 !important;
}

a
{
	text-decoration: none !important;
    color: black !important;
    display: inline-block !important;
    height: 100% !important;
}
a:only-of-type
{
	width: 90% !important;
}
a:first-child
{
	width: 90% !important;
}
a:nth-child(4)
{
	width: 90% !important;
}
a:hover
{
	text-decoration: underline !important;
}
.titlebg a
{
	color: white !important;
    background: none !important;
}

#message {
	width: 90% !important;
	margin-left: 4% !important;
}

textarea
{
	background-color: #c1c2e3 !important;
	border: 1px solid #808080 !important;
}
input
{
	background-color: #c1c2e3 !important;
	border: 1px solid #6D6F9F !important;
	border-radius: 3px !important;
}
input[type="submit"]:hover
{
	background-color: #D1D2EF !important;
}
}
