css:
@-moz-document domain("fxp.co.il") {
div#inlinemod_formctrls, div#yui-gen9.navpopupmenu.popupmenu, div#yui-gen6.navpopupmenu.popupmenu.nohovermenu
    {
        position:fixed;
        right:20px;
        top:50px;
        z-index: 10000000;
    }
div#inlinemod_formctrls span.ctrlcontainer
    {
       font-family: Segoe UI, Tahoma !important;
       padding: 5px 15px 5px 10px !important;
    }
    
li#moderationtools 
    { 
        right: 20px;
        top: 80px;
        background: url('http://fcdn.co.il/images_new/gradients/generic_button.png') repeat-x scroll left top #E9E9E9; 
        width: 100px; 
        text-align: center; 
        padding: 4px 14px 4px 14px !important; 
        border: 1px solid #A8A8A8; 
        position: fixed; 
        z-index: 1000000; 
    }

li#moderationtools h6 a 
    { 
        font: bold 11px Segoe UI !important; 
  		font-family: Segoe UI, Tahoma !important;
    }
    
html, body
      {
          background: /*[[bgColor]]*/ !important;;
      }


div.toplogin.greengr, div.toplogin.graygr
    {
        position:fixed;
        top:0px;
        left: calc(70% - 100%);
        padding-right: 100%;
        width:100%;
        padding-top: 10px;
        padding-bottom: 15px;
        z-index:10000;
        border-radius: 0px;
        background: #fff !important;
        text-align:center;
        font-family: Tahoma !important;
        box-shadow: 0px 5px 10px #c4c4c4;
    }

div#topmenunew
    {
    margin-top: 35px;
    }
 
span.icons
    {
       background-image: url("http://img850.imageshack.us/img850/7812/tbo8.png") !important;
    }
    
span.icons + span, span.icons + span + span
    {
        font-family: Alef, Arial;
        color: #fff !important;
        font-size: 100%;
    }
    
div#topmenunew div.first.barssize.bluegr, div#topmenunew div.first.barssize.bluegr ul#top-menu.pop li, .blockhead
      {
          background: /*[[mainColor]]*/ !important;
          color: /*[[secondColor]]*/;
          font-family: Tahoma !important;
          font-weight: Bold;
      }

div#topmenunew div.first.barssize.bluegr
    {
       border-radius: 0px !important;
    }

div#topmenunew div.first.barssize.bluegr ul#top-menu.pop li a
    {
        transition: background 0.5s;
        font-family: Segoe UI, Tahoma;
        font-size: 16px;
    }
div#topmenunew div.first.barssize.bluegr ul#top-menu.pop li a:hover
    {
        background: /*[[secondColor]]*/ !important;
    }
	
div#topmenunew div.first.barssize.bluegr ul#top-menu.pop li.popupmenu ul li a:hover
    {
        background: /*[[mainColor]]*/ !important;
    }
    
form#navbar_search input.search_new.rounded
    {
        border-radius: 5px !important;
    }
    
div.sec.barssize.graygr, div.sec.barssize.graygr li
    {
        background: /*[[secondColor]]*/;
        color: #fff !important;
        border-radius:0px !important;
        border: none !important;
        padding-left: 2px;
    }
div#slide div.big-image div.first
    {
        background: /*[[secondColor]]*/;
        border-bottom-color: /*[[mainColor]]*/;
        font-family: Segoe UI, Tahoma;
    }
    
div#slide div.big-image div.image_text_main
    {
        background: /*[[secondColor]]*/ !important;
        border-top-color: /*[[mainColor]]*/;
        font-family: Segoe UI, Tahoma;
    }
div#slide div.images_sik.skc_1 div.textsik,
div#slide div.images_sik.skc_2 div.textsik,
div#slide div.images_sik.skc_3 div.textsik,
div#slide div.images_sik.skc_4 div.textsik,
div#slide div.images_sik.skc_5 div.textsik,
div#slide div.images_sik.skc_6 div.textsik,
div#slide div.images_sik.skc_7 div.textsik,
div#slide div.images_sik.skc_8 div.textsik,
div.textsik,
div.textsik:hover
    {
        background: /*[[secondColor]]*/;
        border-top-color: /*[[mainColor]]*/;
        font-family: Alef, Arial;
    }
    
div.fav_div
    {
        font-family: Tahoma;
        font-size: 1em;
    }
    
h2.forum_title
    {
       font-family: Segoe UI, Tahoma !important;
       font-weight: normal !important;
    }
    
div.gu + script + div h1
    {
        display: none;
    }
    
div.ftitle.bluegr, div.ftitle.fixrd
    {
       background: /*[[secondColor]]*/ !important;
       transition: background 0.5s;
    }
    
div.ftitle.bluegr a, div.ftitle.fixrd a
    {
        font-family: Segoe UI, Tahoma;
        font-size: 15px !important;
    }
 
div.ftitle.bluegr:hover, div.ftitle.fixrd:hover
      {
          background: /*[[mainColor]]*/ !important;;
      }

#newthreadlink_top, #newthreadlink_bottom, #newreplylink_top, #newreplylink_bottom, #thread_controls
    {
        background: /*[[mainColor]]*/;
        transition: background 0.5s;
    }
#newthreadlink_top:hover, #newthreadlink_bottom:hover, #newreplylink_top:hover, #newreplylink_bottom:hover, div.threadlisthead.table, div.tcat.bluegr
    {
        background: /*[[secondColor]]*/ !important;
    }

div.posthead-date
    {
        background: /*[[secondColor]]*/ !important;;
    }
div.fullwidth h3#quickreply_title, div.titleshowt.greengr, li#post_ div.greengr
    {
        background: /*[[mainColor]]*/ !important;
        color: #fff;
    }
div.titleshowt.greengr span, div.titleshowt.greengr h1, li#post_ div.greengr h1
    {
        color: /*[[secondColor]]*/;
    }
div.fullwidth h3#quickreply_title img
    {
        content: url("http://img850.imageshack.us/img850/2002/vrc2.png");
    }

div.rating0.nonsticky
    {
       transition: background 1s !important;;    
    }
    
div.rating0.nonsticky:hover
      {
          background: #f0f0f0 !important;;
      }
    
/* ads */
#adfxp, div#right_tower_160, div#left_tower_160, div.dfxp, .coal, .proper, .related_right, [id*="left_tower"], [id*="right_tower"], .final, #div-gpt-ad-1386670391040-2, .rate, .gun, .plain:not(code), .laid, .vowel, .drove, .guide, .danger, .market, .expect, .certainly, .hungry, .average, .empty, .band, .captain, .phrase, .jump, .slow, .correctly, .live, .replied, .easier, .proud, .breakfast, .express, [id*="tt-wrapper"], .mine, .billy, .completely, .tube, .cabin, .coat, .entire, .california, #right_tower_300 + div, div.tower_right + div, div.tower_left + div, div.tower_left_adse + div
    {
        display: none !important;
    }
    
div#right_nav_arrow, div#left_nav_arrow
    {
        font-family: Alef, Tahoma, Arial;
        background: /*[[mainColor]]*/;
        transition: 0.3s background;
        top: 60px !important;
        position: fixed !important;
    }
    
div#right_nav_arrow:hover, div#left_nav_arrow:hover
    {
        background: /*[[secondColor]]*/;
    }
    
/* ##### Chat start ##### */
div#cometchat_base
    {
        border: none;
        background: /*[[secondColor]]*/;
    }
    
div.cometchat_user_shortname, span#cometchat_userstab_text, span.cometchat_tab, span#cometchat_userstab, .cometchat_tabmouseover
    {
        color: #FFF;
        text-shadow: none;
        transition: background 0.2s;
    }
    
div#cometchat_chatboxes_wide span.cometchat_tab:hover, span#cometchat_userstab:hover, span#cometchat_userstab:focus, .cometchat_tabclick, .cometchat_userstabtitle, .cometchat_tabtitle, .cometchat_tabmouseover, .cometchat_trayclick
    {
        background-color: /*[[mainColor]]*/ !important;;
    }
    
.cometchat_userstabtitle, .cometchat_tabtitle, .cometchat_traytitle
    {
        border: 1px solid /*[[mainColor]]*/ !important;
        background: /*[[mainColor]]*/ !important;;
    }
    
#cometchat_hide
    {
        background-image: url("http://up413.siz.co.il/up2/ifddnzq2jgnd.png");
    }
    
#cometchat_optionsbutton_icon
    {
        background-image: url("http://up413.siz.co.il/up3/yewidmzndf0m.png");
    }
    
#cometchat_trayicon_scrolltotop
    {
        background-size: 16px;
        background-repeat: no-repeat;
        background-position: center;
        background-image: url("http://up413.siz.co.il/up2/hmgr1dzlmdlz.png");
        width: 10px;
    }
    
#cometchat_trayicon_scrolltotop img
    {
        display: none;
    }
    
#cometchat_trayicon_share
    {
        background-size: 16px;
        background-repeat: no-repeat;
        background-position: center;
        background-image: url("http://up413.siz.co.il/up3/0znqttikyoyd.png");
        width: 10px;
    }
    
#cometchat_trayicon_share img, #cometchat_trayicon_share .cometchat_trayicontext
    {
        display: none;
    }
    
#cometchat_trayicon_games
    {
        background-size: 16px;
        background-repeat: no-repeat;
        background-position: center;
        background-image: url("http://up413.siz.co.il/up3/ilwdtyy2oxyi.png");
        width: 10px;
    }
    
#cometchat_trayicon_games img, #cometchat_trayicon_games .cometchat_trayicontext
    {
        display: none;
    }
    
#cometchat_trayicon_home
    {
        background-size: 16px;
        background-repeat: no-repeat;
        background-position: center;
        background-image: url("http://up413.siz.co.il/up2/2gynzkmzymzy.png");
        width: 10px;
    }
    
#cometchat_trayicon_home img, #cometchat_trayicon_home .cometchat_trayicontext
    {
        display: none;
    }
}
