css:
@-moz-document url-prefix("http://www.nicovideo.jp/my") {
.nicorepo .nicorepo-next-page {
    border-top: none;
}
.headerBanner,
#pc_video_my_text_west,
#pc_video_my_text_west + div,
#web_pc_footer,
.topic_wrap,
.log-reslist,
.log-subdetails {
    display: none !important
}
.log-mylist-added-video {
    display: none !important
}
}

@-moz-document url-prefix("http://www.nicovideo.jp/watch/") {
.react-grid-Grid {
    min-height: 400px !important;
}
.is-large .react-grid-Grid {
    min-height: 580px !important;
}
@media screen and (max-width:1391px) {
    .is-large .react-grid-Grid {
        min-height: 520px !important;
    }
}
.MiniBannerAdContainer,
.SwitchToFlashLead,
.BottomContainer,
.FooterContainer {
    display: none !important;
}
}

@-moz-document url-prefix("http://www.nicovideo.jp/ranking") {
/*---------------------------------------ランキング----------------------------------------*/
/*----------------------------------------細部修正----------------------------------------*/
/*背景白だと目が痛い*/

body,
header.header,
div.rankingHeader {
    background-color: #FDFDFD !important;
}
/*ヘッダー周り*/

header.header div.inner {
    padding: 5px 0 !important;
}
header.header div.inner div {
    margin: 0 !important;
    padding: 0 !important;
}
header.header div.siteHeader div.inner div.pageLogo {
    margin-right: 5px !important;
    width: 219px !important;
}
header.header div.siteHeader div.inner div.pageLogo h1.logo {
    float: left !important;
}
header.header div.rankingHeader {
    border-bottom: none !important;
    margin-top: 5px !important;
    padding-bottom: 5px !important;
}
header.header div.rankingHeader div.setting div.rankingBtn {
    margin-bottom: 2px !important;
}
/*ランキングの枠部分とブロック*/

div.container {
    padding: 10px 0 !important;
}
div.main {
    border-top: 1px solid #CCC !important;
    border-right: 1px solid #CCC !important;
    padding: 0 !important;
    width: 1020px !important;
}
div.main section.content {
    margin: 0 !important;
}
li.item.videoRanking {
    border-bottom: 1px solid #CCC !important;
    border-left: 1px solid #CCC !important;
    background-color: #FCFCFC;
    margin: 0 !important;
    padding: 0 !important;
    width: 255px !important;
    height: 158px !important;
    position: relative !important;
    z-index: 0 !important;
}
/*ブロック要素並べ替え*/

li.item.videoRanking div.rankingNumWrap {
    padding: 0 0 0 2px !important;
    width: auto !important;
    float: right !important;
}
li.item.videoRanking div.rankingNumWrap p.rankingNum {
    padding: 5px 5px 0 0 !important;
    text-align: right !important;
    color: #CCC !important;
    font-size: 450% !important;
    position: relative !important;
    z-index: -1 !important;
}
li.item.videoRanking div.videoList01Wrap {
    margin: 0 0 0 5px !important;
    width: 150px !important;
    float: none !important;
}
li.item.videoRanking p.adComment {
    left: 5px !important;
}
li.item.videoRanking div.videoList01Wrap p.itemTime {
    margin: 2px 0 !important;
}
.video .uadlevel1 .itemThumbWrap:before,
.video .uadlevel2 .itemThumbWrap:before,
.video .uadlevel3 .itemThumbWrap:before,
.video .uadlevel4 .itemThumbWrap:before,
.video .uadlevel5 .itemThumbWrap:before,
.video .uadlevel6 .itemThumbWrap:before,
.video .uadlevel7 .itemThumbWrap:before,
.video .uadlevel8 .itemThumbWrap:before,
.video .uadlevel9 .itemThumbWrap:before,
.video .uadlevel10 .itemThumbWrap:before {
    background: none !important;
}
li.item.videoRanking div.itemThumbBox,
li.item.videoRanking div.itemThumb,
li.item.videoRanking div.itemThumb .itemThumbWrap {
    margin: 0 !important;
    padding: 0 !important;
    width: 117px !important;
    height: 66px !important;
}
li.item.videoRanking div.itemThumb img {
    margin: 0 !important;
    padding: 0 !important;
    width: auto !important;
    height: 66px !important;
}
li.item.videoRanking div.itemThumbBox span.videoLength {
    bottom: 1px !important;
    right: 1px !important;
}
li.item.videoRanking div.itemContent {
    float: left !important;
}
li.item.videoRanking div.itemContent p.itemTitle,
li.item.videoRanking div.itemContent div.itemData,
li.item.videoRanking div.itemContent div.wrap p.itemDescription.ranking {
    margin: 1px 5px !important;
    width: 246px !important;
    font-size: 100% !important;
}
li.item.videoRanking div.itemContent div.wrap p.itemDescription.ranking {
    height: auto !important;
    font-size: 90% !important;
    position: absolute !important;
    top: 124px !important;
}
li.item.videoRanking div.itemContent div.itemData {
    position: absolute !important;
    top: 18px !important;
    left: 120px !important;
}
li.item.videoRanking div.itemContent div.itemData li {
    font-size: 94% !important;
    color: #555 !important;
    float: none !important;
}
/*----------------------------------------細部修正----------------------------------------*/
/*----------------------------ランキングを見るのにお前たちはいらん---------------------------*/
/*ヘッダの要らない子達*/

span#siteHeaderUserIconContainer,
div#siteHeaderNotificationContainer,
span.siteHeaderNicopo,
/*ﾋﾀﾞﾘｳｴｻﾝ...*/

span.hidariue,
/*広告*/

div.banner,
/*ランキング設定等々必要だったら消してね*/
/*div.rankingHeader,*/

div.menuBar,
/*新検索β()*/

div.ssLink,
/*広告ポイント様*/

li.item.videoRanking div.rankingNumWrap p.rankingPt,
/*コメント先見しないですしおすし*/

li.item.videoRanking div.itemContent div.wrap p.itemComment.ranking,
/*動画のランキングだけ見れればそれでええんやで*/

div.inner div.sub,
div.ads,
/*ﾌｯﾀｰｻﾝ*/

footer.footer {
    display: none !important;
}
/*----------------------------ランキングを見るのにお前たちはいらん---------------------------*/
}
