css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
.comhead {
    font: 13px/1.5 OpenSans, sans-serif;
    margin-left: 3px;
}

.comment {
    display: block;
}

.comment font {
    font: 13px/1.5 OpenSans, sans-serif;
}

.comment pre {
    overflow: visible;
}
    
a:link {
    color: #ccc;
}

.subtext {
    line-height: 1.7;
}

.subtext a:hover {
    border-bottom: 1px solid;
    text-decoration: none;
}

.subtext a:nth-of-type(2) {
    color: #995500;
}

.title {
    font-weight: 200;
}

.title a:hover {
    border-bottom: 1px solid;
}

.title a:visited {
    color: #666;
}

.votearrow {
    margin-top: 7px;
}

.yclinks {
    color: transparent;
}

.yclinks a {
    font: 13px OpenSans, sans-serif;
    padding: 0 4px;
}

[align="right"].title {
    color: transparent;
    padding: 0 4px 0 0 ;
}

[align="right"].title + td {
    padding: 1px 5px 0 0;
    position: relative;
    width: 20px;
}

[id^="up_"] img {
    padding: 3px;
}

a {
    color: #ddd;
}

a[href^="reply?id"] {
    background-color: rgba(100, 100, 100, 0.05);
    border-radius: 3px;
    color: #666;
    display: inline-block;
    float: right;
    padding: 3px 5px;
    text-decoration: none;
}

a[href^="user?id"] {
    font-weight: 500;
    padding: 0;
}

body {
    background: #333;
    margin: 0;
    padding: 0;
}

body > center > table {
    background-color: transparent;
    position: relative;
    width: 1110px;
}

/* header */

/* position td */

/* vote td */

/* search */

font {
    font: 15px/1.5 OpenSans, sans-serif;
}

font u a {
    background-color: #995500;
    color: #fafafa;
}

font[color="#000000"] {
    color: #ddd;
}

form[action*="hnsearch.com"] {
    color: transparent;
}

input, textarea {
    background-color: #FFF;
    border: 1px solid #DDD;
    font: 13px OpenSans, sans-serif;
    padding: 10px;
}

input:focus, textarea:focus {
    border: 1px solid #DDD;
    outline: none;
}

input[name="q"] {
    color: #666;
    left: 150px;
    position: absolute;
    top: 12px;
    width: 190px;
}

input[type="submit"], input[type="reset"], input[type="button"] {
    background-color: rgba(100, 100, 100, 0.1);
    border: none;
    box-shadow: none;
    color: #444;
}

span.comhead a {
    font-weight: 700;
}

td > center > form {
    display: none;
}

td > center > span {
    display: none;
}

td form input {
    background-color: #995500;
    color: #f5f5f5;
}

td form textarea {
    background-color: #474747;
    color: #ebebeb;
}

td, .pagetop {
    font: 13px/1.5 OpenSans, sans-serif;
}

td[bgcolor="#ff6600"] {
    background-color: transparent;
    padding: 13px 0 10px 0;
}

td[bgcolor="#ff6600"] * {
    vertical-align: middle;
}

td[bgcolor="#ff6600"] .pagetop a:not([href="news"]) {
    display: inline-block;
    padding: 4px;
}

.pagetop a[href="news"] {
    font-size: 12pt;
}

td[bgcolor="#ff6600"] a {
    color: #666;
}

td[bgcolor="#ff6600"] b a {
    color: #666;
    display: inline-block;
    font-weight: normal;
    letter-spacing: -1px;
    margin-top: -7px;
    width: 109px;
}

td[bgcolor="#ff6600"] font {
    color: #999;
}

td[bgcolor="#ff6600"] img {
    height: 16px;
    opacity: 0.7;
    width: 16px;
}

td[bgcolor="#ff6600"] td:nth-of-type(2) .pagetop {
    color: transparent;
}

td[bgcolor="#ff6600"] td:nth-of-type(2) .pagetop b {
    color: #666;
}

tr > td > a > img {
    margin-top: -7px;
}

tr[style="height:5px"] {
    height: 13px;
}

center > a:nth-child(3) {
    border-bottom: none !important;
}

center > a:first-child:before {
    content:"▲";
}

center > a:nth-child(3):before {
    content:"▼";
}

center > a:nth-child(3):before,
center > a:first-child:before {
    color: #777;
}

.votearrow {
    display: none !important;
}

}
