css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("badgame.net") {

    div.douj {
    font-family: 'Bangers', cursive;
    color: #000;
    font-size: 1.8rem;
    line-height: 1em;
    background-color: white;
    border: 3px solid #242424;
    padding: 16px;
    max-width: 160px;
    text-align: center;

    -webkit-box-shadow:  3px 3px 4px 1px rgba(0, 0, 0, 0.3);
    box-shadow:  3px 3px 4px 1px rgba(0, 0, 0, 0.3);
}
/* By default (td, body..) use verdana in black. */
body, td, th , tr
{
    color: #fff !important;
}

/* The main body of the entire forum. */
body
{
    background-color: black !important;
}

/* Default font sizes: small (8pt), normal (10pt), and large (14pt). */
.smalltext
{
    color: white !important;
}
.middletext
{
    color: #fff !important;
}
.normaltext
{
    color: #ccc !important;
}
.largetext
{
    color: #ccc !important;
}

div#banner-area {
    background: #000 !important;
}

div#banner-area img {
    border: 1px solid #fff !important;
}

div#news-area {
    border: 1px solid #fff;
    background-color: #fff;
}

div.topic-search {
    background: #000 !important;
    color: white;
}

.userinfo {

    background: #4c4c4c !important;
    color: #ccc !important;
}

.userinfo b
{
    color: #fff !important;
}

.postinfo, .responses {
    border-top: 1px solid rgba(0, 0, 0, 1) !important;
}

.postinfo {
    border-right: 1px solid rgba(0, 0, 0, 0.2) !important;

    background: #4c4c4c !important;
    color: #ccc !important;
}

.thread-title {
    color: #ccc !important;
}

.thread-row a {
    color: #fff !important;
}

/* Normal, standard links. */
a:link
{
    color: #fff !important;
}
a:visited
{
    color: #fff;
}
a:hover
{
    color: #fff !important;
}

a.nav, a.navPages {
    text-shadow: none !important
}

/* Input boxes - just a bit smaller than normal so they align well. */
input, textarea, button
{
    color: #000;
}
input, button
{
    font-size: 90%;
}

/* The "Quote:" and "Code:" header parts... */
.quoteheader, .codeheader, .nwsheader, .spoilerheader, .tldrheader
{
    border: 1px solid #000 !important;
    
    color: #000 !important;
}
.quoteheader {
    background-color: #000 !important;
}

.quoteheader, .quoteheader a
{
    color: #fff !important;
}

/* A quote, perhaps from another post. */
.quote
{
    color: #fff !important;
    background: #666 !important;
}
    
.nwsheader
{
    background-color: #efb5d4 !important;
    color: #fff;
}
    
.nwsblock, .spoilerblock, .tldrblock, .code, .quote {
    border-bottom: 1px solid #4c4c4c !important;
    border-left: 1px solid #4c4c4c !important;
    border-right: 1px solid #4c4c4c !important;
}

.nwsblock
{
    color: #fff !important;
    background-color: #96728f !important;
    border-top: 0;
}
    
.tldrblock
{
	background-color: #000;
	font-family: "Georgia", "Century Schoolbook L", "Times New Roman", "Times", serif;
	border: 1px solid #242424;
	border-left: 3px solid #242424;
	border-top: 0;
	font-size: 1.1em;
	line-height: 160%;
}

/* Alternating backgrounds for posts, and several other sections of the forum. */
.windowbg, #preview_body
{
    background-color: #4c4c4c !important;
    color: #999 !important;
}

.windowbg2
{
    background-color: #4c4c4c !important;
    color: #fff !important;
}

.windowbg3
{
    color: #fff !important;
    background-color: #4c4c4c !important;
}
    
    .threadrow:nth-of-type(odd) .windowbg, .threadrow:nth-of-type(odd) .windowbg2 {
    background: #4c4c4c !important;
}

.threadrow:nth-of-type(even) .windowbg, .threadrow:nth-of-type(even) .windowbg2 {
    background: #4c4c4c !important;
}


/* These are used primarily for titles, but also for headers (the row that says what everything in the table is.) */
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td
{
    color: #fff !important;
    font-style: normal;
    background: #4c4c4c !important;
    border: solid 1px #111 !important;
}
.titlebg, .titlebg a:link, .titlebg a:visited
{
    font-weight: bold;
    color: black;
    font-style: normal;
}

.titlebg a:hover
{
    color: #fff;
}

/* same as titlebg, but used where bold text is not needed */
.titlebg2 a:link, .titlebg2 a:visited
{
    color: #ccc;
    font-style: normal;
}

.titlebg2 a:hover
{
    color: #fff !important;
}

/* This is used for categories, page indexes, and several other areas in the forum.
.catbg and .catbg2 is for boardindex, while .catbg3 is for messageindex and display headers*/
.catbg , tr.catbg td , .catbg3 , tr.catbg3 td
{
    /* background: url(images/catbg.jpg) #88A6C0 repeat-x; */
    background: #4c4c4c !important;
    color: #fff !important;
}

.catbg2, tr.catbg2 td
{
    background: #000 !important;
    color: #FFF !important;
}

.catbg, .catbg2, .catbg td {
    border: 1px solid #4c4c4c !important;
}

.catbg .smalltext {
    color: #000 !important;
}

.catbg3, tr.catbg3 td, .catbg3 a:link, .catbg3 a:visited
{
    color: #fff !important;
}

.catbg3 td {
    border: 1px solid #000 !important;
}

.catbg a:link, .catbg a:visited , .catbg2 a:link, .catbg2 a:visited, .catbg, .catbg td div
{
    color: #fff !important;
}

.catbg a:hover, .catbg2 a:hover, .catbg3 a:hover
{
    color: #fff;
}

/* This is used for tables that have a grid/border background color (such as the topic listing.) */
.bordercolor
{
    background: #000 !important;
}

/* This is used on tables that should just have a border around them. */
.tborder {
    background: #000 !important;
    border: #fff !important;
}

.tborder .threadrow {
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}

/* definitions for the main tab, active means the tab reflects which page is displayed */
.maintab_first, .maintab_back, .maintab_last, .maintab_active_first, .maintab_active_back, .maintab_active_last
{
    text-transform: uppercase;
    vertical-align: top;
}

.maintab_back, .maintab_active_back {

    border-left: 1px solid #111 !important;
    border-right: 1px solid #111 !important;
    border-bottom: 1px solid #111 !important;
}

.maintab_back
{
    color: #4c4c4c !important;
    background: #000 !important;
}

.maintab_active_back
{
    color: #4c4c4c !important;
    background: #4c4c4c !important;
}

.maintab_back:hover, .maintab_active_back:hover, .maintab_back:hover a, .maintab_active_back:hover a
{
    background: #000 !important;
    color: #4c4c4c !important;
}

.mirrortab_back a:hover, .mirrortab_active_back a:hover
{
    color: #4c4c4c;
    text-decoration: none;
}

.windowbg a, .windowbg2 a, .windowbg3 a 
{
    color: #fff !important;
}

.threadrow .windowbg2 a
{
    color: #fff !important;
    font-size: 11px !important;
}

#footerarea * {
    color: #999 !important;
}
    
#quickReplyOptions .windowbg {
    background: #4c4c4c !important;
}
    
#quickReplyOptions .windowbg form#postmodify textarea {
    background: #fff !important;
    border: 1px solid #111;

}

.thread-list-pages {
    color: #fff !important;
}

.thread-list-pages a {
    color: #fff !important;
}

#bodyarea table tr td span.middletext {
    color: #fff !important;
}

#creator .windowbg .smalltext, #creator .windowbg2 .smalltext, #creator .windowbg2 .smalltext i, #bodyarea table tbody tr td table.bordercolor tbody tr.windowbg .smalltext, .threadrow .windowbg, #postMoreOptions *, div.post {
    color: #fff !important;
}

.nav, .nav:link, .nav:visited
{
    color: #c4c4c4 !important;
}

a:hover {
    color: #999 !important;
}

a.nav:hover
{
    color: #fff !important;
}

a.navPages, a.navPages:visited {
    color: #fff !important;
}

a.navPages:hover {
    color: #999 !important;
}
    

.genericPanel {
    background-color: #6e6e6e;
    border: 1px solid #888;
    margin: 0 auto;
    padding: 12px;
    width: 680px;
}
    

.tips {
    background-color: #B2AFAF;
    box-shadow: 3px 3px 5px 0 rgba(0, 0, 0, 0.2);
    padding: 8px;
}
    
}
