css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("transhumanity.net")

{
    
/*
Singularity Utopia
@2045singularity
22 Oct 2015
*/    
 
iframe
   
   {
      border-radius: 3px !important;
   }
   
.header.section.small-padding.bg-dark.bg-image
   
   {
      background-image: url("https://i.imgur.com/Ii6ONk2.jpg") !important;
   }
   
.blog-description
   
   {
      color: #FFDD7C;
      text-shadow: 1px 1px 1px black, 1px 1px 4px black;
   }
   
.post-excerpt
   
   {
      padding: 7% 5%;
   }
   
.posts .post.type-post.status-publish 
   
   {
      border: 1px solid lightgrey;
      border-radius: 6px;
      padding: 6px;      
      box-shadow: #CFCFCF 0px 3px 23px -4px;
      transition: 0.3s all ease-in-out;
   }
   
.posts .post.type-post.status-publish:hover
   
   {
      box-shadow: #B3B3B3 0px 3px 23px 0px;
      border-color: #8FA0C4;
   }
   
.posts .post.type-post.status-publish .post-header 
   
   {
      padding: 4% 6% 5% 6%;
   }

.posts .post-header .post-title a  
   
   {
      text-shadow: none !important;
   }
   
.hentry, .post

    {
       box-shadow: none;
    }

.bottomcontainerBox

    {
       border-radius: 3px;
       border-color: lightblue !important;
       box-shadow: 0px 15px 14px -13px #D3D3D3;
       margin-bottom: 30px;
       padding: 10px 10px 0px 10px;
       height: auto !important;
       display: inline-block;
    }

.bottomcontainerBox > div
   
   {
      padding: 0px 3px 0px 0px !important;
      margin: 0px 3px 0px 2px !important;
      overflow: visible !important;
      width: auto !important;
      display: inline-block;
   }
   
.fleft
   
   {
      padding: 1% 4%;
      background: white;
      border-radius: 6px;
      border: 1px solid #E3E3E3;
   }

.section-inner 
   
   {
      max-width: 98%;
      width: 1700px;
   }   
   
.post-header
   
   {
      padding: 5% 1% 6% 1%;
      border-bottom-width: 0px !important;
      margin:0px;
   }
   

.post-header .post-title a 

    {
       text-shadow: 0px 3px 4px #C9C9C9;
    }
    
.post-header .post-title a:hover
    
    {
        text-shadow:none;
    }

.single .post-content 

    {
       padding: 9% 0px !important;
    }

body 

    {
       font-size: 13pt;
    }
       
.post-meta-container
   
   {
      border-radius: 8px;
   }
   
@keyframes transhumantitle
    
{           
    0% {text-shadow: none;}
    15% {text-shadow: none;}
    25% {text-shadow: none;}   
    50% {text-shadow: purple 0px 0px 9px, red 0px 0px 30px, yellow 0px 0px 5px;}
    70% {text-shadow: purple 0px 0px 51px, red 0px 0px 41px, yellow 0px 0px 71px;}
    100% {text-shadow: none;}
}

@-webkit-keyframes transhumantitle
    
{           
    0% {text-shadow: none;}
    15% {text-shadow: none;}
    25% {text-shadow: none;}   
    50% {text-shadow: purple 0px 0px 9px, red 0px 0px 30px, yellow 0px 0px 5px;}
    70% {text-shadow: purple 0px 0px 51px, red 0px 0px 41px, yellow 0px 0px 71px;}
    100% {text-shadow: none;}
}
        
.blog-title a

    {
       font-size: 32pt;
       text-shadow: none;
       animation-name: transhumantitle;
       animation-duration: 10s;
       animation-timing-function: ease-in-out;
       animation-iteration-count: infinite;
       transition: all 0.5s ease-in-out;
       -webkit-animation-name: transhumantitle;
       -webkit-animation-duration: 10s;
       -webkit-animation-timing-function: ease-in-out;
       -webkit-animation-iteration-count: infinite; 
    }
    
.blog-title a:hover
    
    {
       animation: none;
    }

.featured-media img

    {
       border-radius: 5px;
       box-shadow: grey 0px 30px 17px -24px;
       transition: all 1.5s ease-in-out;
       width:100%;
    } 
    
.wrapper.section 
    
    {
       padding: 2% 0px 2% 1% !important;
    }    

.sidebar 
    
    {
       background: #E3E3E3 !important;
       border:1px solid #C4C4C4;
       box-shadow: inset white 0px 0px 1px;
    }
    
.post-author p, .comment-content p, .comment-form textarea
    
    {
       font-size:11pt; 
    }
    
.comment-form textarea
    
    {
       line-height: 1.5; 
    }
    
.comment-form textarea:focus, INPUT#url:focus, INPUT#author:focus, INPUT#email:focus
    
    {
       background: #EBEFF5;
       color: black;
       border-radius: 6px 0px 0px 6px;
       border-color: #CCD1D9 !important;
       box-shadow: inset white 0px 0px 1px;
    }
    
.content .comments
    
    {
       padding: 10% 0px 10% 1% !important;
    }
    
.comment-respond
    
    {
        padding: 4%;
    }
     
.comment-inner
   
   {
      border-bottom: 1px solid #D4D4D4;
   }
}
