css:
@-moz-document domain("moodle2.gla.ac.uk") {
.navbar {
  background-color: #397BA6 !important;
  z-index: 2000;
}

#moodleheader {
  background-color: #397BA6 !important;
  padding-bottom: 10px !important;
  font-family: 'HelveticaNeue',sans-serif !important;
  font-weight: 100;
  box-shadow: 0 5px 20px rgba(0,0,0,0.1) !important
}

.breadcrumb-button {
  margin-top: -21px !important;
}

body, html {
  font: 100%/1.5em 'HelveticaNeue',sans-serif !important;
}

h1, h2, h3, h4, h5, h6 {
  font-family: 'HelveticaNeue',sans-serif !important;
  font-weight: 100 !important;
  color: #397BA6 !important;
}

.breadcrumb>li>a, .breadcrumb>li+li:before {
  color: #EEE !important;
  font-family: 'HelveticaNeue',sans-serif !important;
}

.breadcrumb {
  background: rgba(255,255,255,0) !important;
}
 
.navbar-brand {
  float: left !important;
  padding: 19px 15px !important;
  font-size: 32px !important;
  color: #fff;
  transition: 0.4s;
}

.navbar-brand:hover {
  text-shadow: white 0px 0px 10px;
  color: white !important;
}
  
.navbar-right {
  float: left !important;
  margin: 4px 0 0 0;
}
#page-navbar {
  width: auto !important;
  padding-left: 5px !important;
  font-family: 'HelveticaNeue',sans-serif !important;
}

#main {
  background: #f1f1f4 !important;
  font-family: 'HelveticaNeue',sans-serif !important;
}

#region-main, #page-marketing {
  font-family: 'HelveticaNeue' !important;
  margin-left: 10px;
  margin-right: -10px;
}

.dropdown {
  -webkit-transition: background 0.5s !important;
}

.dropdown :hover {
  background: #5A9DC9 !important;
  color: #2B485A !important;
}

input[type="submit"] {
  border: none;
  background: #397BA6;
  color: #EEE;
  z-index: 2001;
}

input[type="submit"] :hover {
  color: #397BA6 !important;
}

.navbar-toggle {
  display: none;
}

.block {
  margin-right: 20px;
}
}
