css:
@-moz-document domain("vozforums.com") {
/*VOZ Background Color - phần viền bao quanh phần nội dung, không phải nền đen phía sau*/
  .body {
  	background: #222222 !important; 
  }

  /*Thanh điều hướng của Voz*/
  .navbar {
  	color: #303030;
  }

  /*Các thanh tiêu đề như Thread, Thread Starter,..*/
  .thead, .tfoot { 
      background: #8881A3 !important;
    	font-weight: normal !important;
    	padding: 6px !important;
  }
    
  /*Thanh bar chứa Control menu như UserCP, Search, ...*/
  .vbmenu_control { 
      background: #8881A3 !important;
    	border: 0px solid !important;
    	font-weight: normal !important;
    	padding: 8px 8px 8px 8px;
  }

  /*Popup bật ra khi nhấn mũi tên xuống. Ví dụ: Gotopage, Search popup*/
  .vbmenu_popup { 
    	display: inline-block;
  	  border: 0px solid !important;
      background: #ffffff !important;
    	font-weight: normal !important;
  }

  /*Màu khi hover các thẻ lựa chọn trong popup*/
  .vbmenu_hilite { 
  	  background: #48C48A !important;
    	font-weight: normal;
  }

  .vbmenu_hilite a:hover { 
  	color: #FFFFFF !important;
    	font-weight: normal;
  }

  /*Màu mặc định của các thẻ lựa chọn trong popup*/
  .vbmenu_option { 
  	background: #FFFFFF;
    color: #000000 !important;
  }

  /*Viền bao ngoài các thead, tcat, ...*/
  .tborder { 
    	border: 0px solid #C0C0C0 !important;
  }

  /*Màu nền mặc định ở list các topic trong mỗi f*/
  .page { 
  	background: #F5F5F5 !important;
    	box-shadow: none;
  }

  /*Thanh phân loại, ví dụ: Đại sảnh, máy tính để bàn*/
.tcat { 
  background: #8881A3 !important;
    color: #FFFFFF;
}

  /*Background của các f*/
  .alt1Active, .alt1Active_didongnet, .alt1Active_zotac, .alt1Active_brother { 
  	background: #F5F5F5 !important;
  }    

/*Màu nền ô chứa thông tin về số thread của mỗi f ở trang chủ, ...*/
.alt1 { 
  background: #FFFFFF !important;
    border: none;
    color: #000000 !important;
}     

/*Màu nền phần chứa thông tin của các thím, ...*/
.alt2 { 
  background: #F0F0F0 !important;
  border: none;
}  

  table[style="margin-bottom:3px;padding:0px 10px 0px 0px"] {
  	padding: 0px 0px 0px 0px !important;
  }

  /*Trong phần QuickReply, panelsurround là phần bao ngoài cùng, panelsurround chứa panel, hai phần này bao ngoài khung soạn thảo cmt*/
  .panelsurround { 
    	width: 100% !important;
  }
  .panel {
      background: #E4E7F5 !important;
  	border: none !important;
  }

  /*CSS chung cho các button*/
  .button { 
    	background: #ACACAC !important;
      border: none;
      color: #ffffff;
      height: 30px;
    	transition: background-color ease-in-out .3s;
  }

  .rainbow_button { 
    	background: #ACACAC !important;
      border: none;
      color: #ffffff;
    	padding: 4px 5px 5px 5px;
    	transition: background-color ease-in-out .3s;
  }    

  .cmnw .btn, .showimage { 
    	background: #ACACAC !important;
      border: none;
      margin-right: 4px;
      padding: 4px 6px 4px 5px;
      color: #ffffff;
      height: 30px;
    	transition: background-color ease-in-out .3s;
  }

  /*Màu khi mouse over button*/
  .button:hover, .rainbow_button:hover, .cmnw .btn:hover, .showimage:hover { 
  	background: #8881A3 !important;
    	transition: background-color ease-in-out .3s;
  }

  /*Các textbox điền thông tin*/
  .bginput, #rainbow_fontcolor { 
    	background: #f2f2f2;
    	display: INLINE;
    	padding-left: 5px;
  	margin: 0px 2px 0px 2px;
    	border-top: none;
    	border-bottom: none;
    	border-right: none;
    	border-left: 5px solid #B523FF;
    	color: #000000;
    	height: 30px;
    	width: auto;
  	transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
  }

  /*Đặt màu đậm hơn khi focus textbox*/
  .bginput:focus, #rainbow_fontcolor:focus { 
    	background: #ffffff;
    	display: INLINE;
    	padding-left: 5px;
  	margin: 0px 2px 0px 2px;
    	border-top: none;
    	border-bottom: none;
    	border-right: none;
    	border-left: 5px solid #B523FF;
    	color: #000000;
    	height: 30px;
    	width: auto;
  	transition: box-shadow ease-in-out .15s, background-color ease-in-out .15s;	
  }

  /*Fieldset là phần chứa các tùy chọn hoặc các mục bổ sung trong Voz, như: Option khi cmt */
  fieldset { 
    	border: 1px solid #8881A3;
    	color: #505050;
    	margin-top: 30px;
  }

  /*Legend là phần chữ nằm đè lên đường chiều dài phía trên của các Fieldset*/
  legend {
    	background: #8881A3;
    	border: 1px solid #8881A3;
  	color: #f2f2f2 !important;
    	padding: 3px 6px 3px 6px;
  }

  /*Các thẻ select*/
  select { 
    	padding: 3px 0px!important;
  	color: #000000 !important;
    	background-color: #ACACAC !important;
    	border: 0px;
    	transition: all 0.3s ease-out;
  } 

  /*Tên người dùng hiển thị trong mỗi post*/
  .bigusername {
    	font-size: 20px !important;
    	font-weight: 600;
  }

  /*Trang User Profile*/
  /*Tên người dùng*/
  #main_userinfo h1 {
      font-size: 20px !important;
      font-weight: bold;
  }

  /*Xóa khung bên phải, mở rộng phần nội dung Last Post cho khỏi bị đè chữ*/
  TD[width = "160"] {
    	display: none !important;
  }
  [style*=wrap] > STRONG { margin-right: 100px } 


  /*Các tab thông tin bên dưới*/
  ul.tab_list li.tcat {
  	font-weight: 600 !important;
  }

  /*Bo góc cho ảnh Avatar của các thành viên trong Voz*/
  [alt*="'s Avatar"] {
    	border-radius: 5px !important;
  }

  /*Dành cho tự sướng, làm cho avatar của bạn thành hình tròn, nên dùng ảnh có size chiều rộng = chiều cao*/    
  [alt="kataly56's Avatar"] {
  	width: 100px;
    	height: 100px;
    	border-radius: 50% !important;
  }

  /*Bo tròn cho ảnh trong Profile*/
  [alt*="'s Profile Picture"] {
    	border-radius: 50% !important;
  }

  /*Dành cho ai dùng Autopage, đổi màu nền thanh loading của AutoPager*/
  .autoPagerS {
    	background: #656565 !important;
  }


  /*Phần comments*/
  .vBulletin_editor { 
  	background: #E1E1E2 !important;
      border: none !important;
    	color: #000000;
  }

  /*Phần ghi nội dung comment*/
  #vB_Editor_QR_textarea, #vB_Editor_001_textarea, textarea { 
    	background: #f2f2f2;
    	display: block; 
    	border: 0px solid #ccc;
    	color: #000000;
    	box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    	transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    	height: 150px !important; 
    	margin: 9px 0px 0px 0px; 
  }

  #vB_Editor_QR_textarea, #vB_Editor_001_textarea:focus, textarea:focus { 
    	background: #ffffff; 
    	transition: background-color ease-in-out .25s;
  }

  /*Nới rộng khoảng cách giữa 2 hàng icon của bbcode*/
  #vB_Editor_001_controls, #vB_Editor_001_controls > table:nth-child(1), #vB_Editor_001_controls > table:nth-child(2) {
      padding: 3px !important;
  }

  /*Chỉnh màu nền cho các nút BB Code*/
  .imagebutton {
      background: #E1E1E2;
  }
  .imagebutton:hover {
      border: 1px solid #E1E1E2 !important;
    	background: none !important;	
  }

  /*Kích thước ô chọn size*/          
  #vB_Editor_001_size_out {
      width: 47px !important; 
  	height: 30px;
      text-indent: 6px;  
  }

  #vB_Editor_QR_color_bar, #vB_Editor_001_color_bar {
  	min-width: 30px;
      background-color: #009688 !important;
  }

  /*Đổi màu border của các nút mở rộng cho trùng với tông của nền*/
  .alt_pickbutton, #vB_Editor_QR_color_out {
      border-color: #DDDDDD !important;
  }
         
  /*màu nền, màu viền và màu chữ cho các lựa chọn*/          
  .osmilie, .ocolor, .ofont, .osize {
  	background: #FFFFFF !important; 
  	border: #FFFFFF !important; 
  	color: #303030 !important
  } 

  /*màu nền, màu viền và màu chữ cho các lựa chọn khi mouse over*/                    
  .osmilie:hover, .ofont:hover, .osize:hover {
  	background: #8881A3 !important; 
  	border: #8881A3 !important; 
  	color: #FFF !important
  }
   
  /*Đổi màu cho thanh thông báo dung lượng inbox*/
  [title*="Messages stored in this folder"] {
  	background-color: #F44336 !important;
  }
  [title*="Maximum allowed stored messages"] {
  	background-color: #8881A3 !important;
  }
  /*Màu viền ngoài của thanh thông báo dung lượng inbox*/
  #collapseobj_pmlistinfo > tr > td > div > div > fieldset > table > tbody > tr:nth-child(2) > td > table {
  	border: 1px groove !important;
  }

  /*Xóa border bottom trên thanh phân loại số inbox theo ngày*/
  td[ondblclick^="toggle_collapse('pmf0"] {
  	padding: 8px !important;
    	border-left: 5px solid #444444 !important;
    	border-bottom: none !important;
  }

  .alt2[style="padding:0px; border-bottom:1px outset"] {
  	padding: 8px !important;
    	border-bottom: none !important;
  }

  /* Hide Logo - Kamikize */
  #neo_logobar {
    	opacity:0;
    	margin-top:-100px;
  }

  /*CSS chung cho giao diện các thẻ quote, code, php*/
  td[style="border:1px inset"], pre[dir="ltr"], div[dir="ltr"] {
    	border-top: 0px !important;
    	border-right: 0px !important;
    	border-bottom: 0px !important;
    	border-left: 5px solid !important;
    	margin: 10px 0 20px 20px;
    	overflow: hidden;	
  }

  /*Thẻ quote*/
  td[style="border:1px inset"] {
    	border-left-color: #8881A3 !important;
    	background: #E2e2e2 !important;
    	padding-bottom: 20px;
  }
  /*Thẻ code*/
  pre[dir="ltr"] {
    	border-left-color: #F44336 !important;
    	background: #D2D2D2 !important;
    	min-width: 97%;
  }
  /*Thẻ PHP*/
  div[dir="ltr"] {
  	border-left-color: #2196F3 !important;
    	background: #C2C2C2 !important;
    	min-width: 97%;
  }

  /*Tăng khoảng cách giữa các icon Post Reply, thanh phân trang, với phần nội dung chính*/
  .pagenav, [alt="Reply"], [alt="Closed Thread"] {
    	margin-top:10px
  }

  /*Ẩn box GameMobile*/
  #collapseobj_forumbit_46 {
  	display: none;
  }
}

@-moz-document domain("vozforums.com") {
a[href] {
  	color: #965251;
  }
  a.vozsticky, a:hover {
    	color: #FF3D43; 
    	text-decoration: none;
    	-webkit-transition: all 0.3s;
  	transition: all 0.3s;
    	-moz-transition: all 0.3s;
  }
  a.vozsticky:hover {
    	text-decoration: underline !important;
  }

  TD .tcat a:hover {
  	text-decoration: none;
  }

  .thead a:hover {
  	color: #FFFF66;
  }
}

@-moz-document domain("vozforums.com") {
/*Đổi font cho hợp với giao diện phẳng*/

  @font-face {
      font-family: "Segoe UI";
      font-weight: 200;
      src: local("Segoe UI Light");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 300;
      src: local("Segoe UI Semilight");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 400;
      src: local("Segoe UI");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 600;
      src: local("Segoe UI Semibold");
  }
  @font-face {
      font-family: "Segoe UI";
      font-weight: 700;
      src: local("Segoe UI Bold");
  }
  @font-face {
      font-family: "Segoe UI";
      font-style: italic;
      font-weight: 400;
      src: local("Segoe UI Italic");
  }
  @font-face {
      font-family: "Segoe UI";
      font-style: italic;
      font-weight: 700;
      src: local("Segoe UI Bold Italic");
  }

  * {
  	font-family: Segoe UI !important;
    	font-size: 15px !important;
  }

  
}
