css:
@-moz-document url-prefix("https://www.facebook.com") {
/* top nav bar */
#blueBarDOMInspector a[title="Profile"][href^="https://www.facebook.com/"] span,  /* user name */
#blueBarDOMInspector a[href^="https://www.facebook.com/?"] div, /* updates indicator in home button */
#blueBarDOMInspector a[aria-label="Privacy Shortcuts"],

/* search bar */
#blueBarDOMInspector .injectedSearchSuggestion,

/*  ride hand nav bar  */
#pagelet_trending_tags_and_topics,
#pagelet_rhc_footer,
div#rightCol a[aria-label="Show Ticker"],
#rightCol .tickerLineToggleNormal,
div#rightCol div.AdvertiserHomePagelet,
div.ego_section a[href^="/campaign/landing.php"],  /* create an add */


/* left side nav bar  */
#userNav,
div#groupsNav a[title="Create Group"],
div#groupsNav a[title="Find New Groups"],
div#groupsNav a[title="Manage Your Groups"],
div#sideNav div#pagesNav,
div#sideNav div#listsNav,
div#sideNav div#interestsNav,
div#sideNav div#eventsNav,
#sideNav #topicFeedsNav,
#sideNav #appsNav,


#fbDockChatBuddylistNub, /* chat bar */

.fbPhotoSnowliftContainer .ego_section,  /* PYMK in photo popup */


noop
{
  display: none !important;
}
}

@-moz-document url-prefix("https://www.facebook.com/messages") {
/* /messages  */

div[role="main"] div[id^="js"] a[role="checkbox"],  /* Press enter to send checkbox */

noop
{
  display: none !important;
}
}
