	background-image: #4a4aa5;
	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#999), to(#505050));
    	background-image: -webkit-linear-gradient(top, #999, #505050);
	background-image: -moz-linear-gradient(top, #999, #505050);
