/* FLASH NOTICE */


#flash_notice,#flash_alert,#flash_error {
	position: fixed;
	z-index: 9999;
	top: -100px;
	width: 100%;
	text-align: center;
}

#flash_notice .message, #flash_alert .message, #flash_error .message {
	-moz-border-radius: 3px;
	-webkit-border-radius: 3px;
	border-radius: 3px;
	-webkit-box-shadow: 0 1px 4px rgba(0,0,0,0.8);
	-moz-box-shadow: 0 1px 4px rgba(0,0,0,0.8);
	box-shadow: 0 1px 4px rgba(0,0,0,0.8);
	display: inline-block;
	padding: 12px 10px;
	min-width: 400px;
	max-width: 800px;
	color: #fff;
	background-color: transparent;
	border: 0px solid rgba(255,255,255,0.7);
	font-weight: bold;
	font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
	font-size: 12px;
}

#flash_notice .message {
	color: #fff;
	background-color: #4AA556;
	border-color: #000;
}

#flash_error .message, #flash_alert .message {
	background-color: #d9334b;
	border-color: #000;
}

