css:
@-moz-document regexp("http://anilist\\.co/(?!(animelist|mangalist|editor)).*"){
body, footer,.recent{background: white;}
.activity,.activity__body,.activity__nav,.char__row{background: rgba(0, 0, 0, .25) !important;border-radius:15px !important;color: white !important;}
.char-header{background:white !important;}
.forum__row,.user_stats,.fav,.create-thread,.forum__nav,.series-data,.comment__body,.thread__body{background: rgba(0, 0, 0, .25) !important; border-radius:15px !important;}

.noti__link{color: black !important;}
.noti_row.clearfix{background: white !important;}
.char-header.container-fluid.clearfix{background: rgba(0, 0, 0, .25) !important;} 
.sweet-alert.showSweetAlert.visible{background: black !important;}


.fa.fa-reply,.fa.fa-minus,.fa.fa-sort-amount-desc,.fa.fa-plus,.fa.fa-plus::before{color: black !important;}
time-ago{color: white !important;}
p{color: black !important;}
.tag-panel{background: transparent !important;}
.status.ng-pristine.ng-valid.ng-touched{color: black !important;}
.update.popup{background: black !important;}
.edit{color: white !important;}
.banner__header{color: white !important;}
.banner_nav{background: rgba(0, 0, 0, .25) !important;}
a:hover{background: rgba(0, 0, 0, .75) !important;}
.fa.fa-thumb-tack{color: white !important;}
p{color: black !important;}
  
.forum__row__title{color: white !important;}
.user__list{color: white !important;}
.forum__nav{background: rgba(0, 0, 0, .25) !important; color: white !important;}
.row__counts{color: white !important;}
.settings{background: rgba(0, 0, 0, .25) !important; color: black !important;}
.settings__nav{background: rgba(0, 0, 0, .25) !important; color: white !important;}
.active{background: rgba(0, 0, 0, .75) !important;}
.cap{color: white !important;}
.activity__new{background: rgba(0, 0, 0, .25) !important;}
.airing__title,.airing__plus{background: rgba(0, 0, 0, .45) !important;}
.thread__body,.comment__body,.thread__title,.comment__name,.current{color: white !important;}
.data__row,.charsLink,.activity__data{color: white !important;}
.tag{background: white !important; color: black !important;}
.tag:hover{background: white !important;}
time-ago:hover{background: rgba(0, 0, 0, .75) !important;}
.activity__reply{background: rgba(0, 0, 0, .25) !important;}
.activity__reply__name{color: white !important;}
.query-tags:before{border-left: solid 7px transparent !important;} 
.activity__img{margin-top:15px !important;}
.settings{border-radius:15px !important;}
.list__stats{color: white !important;}
.banner__nav{background: rgba(0, 0, 0, .25) !important; color: white !important;}
.reply,.replies{color: white !important;}
.fav__edit{background: rgba(0, 0, 0, .25) !important; color: white !important;}
.ng-pristine.ng-untouched.ng-valid{color: white !important;}
.ad,.recent{background: rgba(0, 0, 0, .25) !important; color: white !important;}
h1,h2,h3,div,b,a,input,p{color: white !important;}
.mark-live{background: black !important; color: white !important;}

.clearfix{color: white !important;}
.charsLink{color: white !important;}

span{color: white !important;}

.header{color: white !important;}
.markEditor.ng-pristine.ng-valid.ng-touched,.mark-live__header,.mark-live,input,textarea,div.mark-live{background: black !important; color: white !important;}
.browse__banner--dark.clearfix{background: rgba(0, 0, 0, .25) !important;}
select{color: black !important;}
.markdown_spoiler::before{background: white !important; color: black !important;}
.markdown_spoiler{background: transparent !important; color: black !important;}


.char__col{border:none !important;}
i.fa.fa-times{right: 20px;top: 15px; color: black;}
i.fa.fa-trash-o.udelete{right: 50px;}
nav,.nav__footer{width:50px !important;}
div#view.clearfix{width:100% !important;top: 0px !important; left:50px !important;}
nav:hover+#container-main {
margin-left: 50px !important;
}
nav:hover .logo__wrap{
  margin-left:0px;width:42px;
}

nav .nav__avatar span {top: 18px;}

nav {
    background: rgba(0, 0, 0, .25);
}

nav .nav__footer {
    background: rgba(0, 0, 0, .75);
    opacity: 1;
}

nav a {color: white;}

nav a:hover {
    background: rgba(0, 0, 0, .75);
    opacity: 1;
    color: white;
}

body {
background: url(http://i.imgur.com/9By6rKI.jpg)
no-repeat center right fixed;
background-size: 1360px 725px;
}
}


@-moz-document url-prefix(http://anilist.co/animelist/){
