css:
@-moz-document domain("theoldreader.com") {
	/*
	.navbar .brand {
		background-image: url(\"image.type\");
	}

	.form-search .btn {
		background-image: url(\"image.type\");
	}

	.subscribe .icon {
		background-image: url(\"image.type\");
	}
	*/

	body, .span3, .nav-collapse, .row-fluid, h2, .home-page .ourstuff a, .label.label-transparent, .hero-unit.posts-filler span, .shortcuts.shortcuts.modal.hide.fade.in, div.hero-unit.no-posts, .dropdown-menu {
		background: #2d2d2d;
		color: #f1f1f1;
	}

	.nav-list li.nav-header a,
	.nav-list li.nav-header a:hover,
	.nav-list li.nav-header a:focus {
		font-size: 12px;
		color: #0099CC !important;
	}

	.container > .row-fluid {
		height: 58px;
	}

	.nav > li > a:hover, .nav > li > a:focus {
		background-color: #2d2d2d;
	}

	.dropdown-menu > li > a {
		color: #FFFFFF !important;
	}

	.dropdown-menu > li > a:hover {
		background: #2d2d2d !important;
	}

	.navbar .nav li .dropdown-menu {
		background: none;
		border: 0px none;
		border-radius: 0 0 0 0;
	}

	.subscribe.open .btn-primary {
		background: none;
	}
	
	.page-header {
		border-bottom: 1px solid #1e1e1e;
	}

	.navbar .nav li a {
		background: #2d2d2d;
		color: #f1f1f1;
	}

	.well {
		background: #1e1e1e;
		border: 1px solid #000;
	}

	a, .post h3 a, .home-page dl dd p a, .nav-list li a {
		color: #f1f1f1;
	}

	.nav-list li.active a {
		color: #0099CC;
	}

	.nav-list li.active a:hover, .home-page .ourstuff ul:first-child li:first-child a {
		color: #0099CC;
	}

	a:hover, a.li:hover, .nav-list strong:hover, .nav-list li a:hover, a.social:hover {
		text-shadow: 0 0 2px #f1f1f1 !important;
		text-decoration: none;
		color: #f1f1f1 !important;
	}

	.post .content, p, div.section, li {
		color: #AAA;
	}

	.post.unread {
		border-left: 4px solid #0099CC;
	}

	element.style {
		background-color: #0099CC;
	}

	.reader .floating {
		background: #2d2d2d;
		padding: 5px 0 10px 0;
	}

	.nav-list li.active a:not(.i):before, element.style, .badge.badge-info, .badge, span.badge, .post .btns .likes a {
		background-color: #1e1e1e;
		color: #8B8B8B;
		border: 1px solid #8B8B8B;
	}

	.label.label-feed {
		background-color: #333;
	}

	.nano>.pane>.slider {
		background: #999;
	}

	a.btn.btn-primary.btn-small {
		background: #0099CC;
	}

	.subscribe .btn-primary {
		background: #333;
		border: 1px solid #000;
		box-shadow: 0 1px 2px #333;
		padding: 8px 20px;
		color: #f1f1f1;
	}

	.subscribe {
		background: #2d2d2d;
	}

	.nav-list li.nav-header.active a:not(.i):before, .nav-list li.active a:not(.i):before {
		border-left: 3px solid #0099CC;
	}

	.btn, .btn.btn-small.btn-read {
		background-color: #333;
		background-image: none;
		color: #f1f1f1;
		text-shadow: none;
	}

	 .btn.btn-small.btn-read:hover, a.btn.btn-mini.unsubscribe.external:hover, a.btn.btn-small.refresh:hover, a.btn.btn-small.read:hover, a.btn.btn-small.btn-share:hover, a.btn.btn-small.btn-like:hover, button.btn.btn-small.post-up:hover, button.btn.btn-small.post-down:hover, a.btn.btn-small.settings.has-tooltip:hover, a.btn.btn-small.settings:hover {
		background-color: #555;
		color: #f1f1f1;
		text-shadow: none !important;
	}

	input.input-xlarge.search-query {
		background: #1d1d1d;
		border: 1px solid #000;
		color: #0099CC;
	}

	.label.label-social {
		background-color: #0099CC;
	}

}
