css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.mmm-tasty.ru/main") {
body{
  background-color:#333 !important;
  background-image:none !important;
}
.sidebar P,
.sidebar H2,
.sidebar A,
.sidebar SPAN{
  color:#fff !important;
  background:transparent  !important;
}


.sidebar H2{
  background:#ccc  !important; 
  padding:2px 12px 0 !important;
  margin:0 -20px 0 -12px !important;
}
.sidebar H2 span,
.sidebar H2 A{color: #000 !important;}
.sidebar_right H2{
  padding:2px 12px 0 !important;
  margin:0 -12px 0 -20px !important;
}
.sidebar_right .sidebar_search FORM{padding-top:10px  !important}

.sidebar #feedback_create {padding-top:20px }
.sidebar #feedback_create FORM{padding-top:10px  !important}

.sidebar .users_nav A.highlight,
.sidebar .vertical_nav  A.highlight{
  text-decoration:none;
  font-weight:bold
}
.sidebar .users{
  padding-top:10px  !important
}
.post_author a{
  background:#ccc !important; 
  color: #333!important
}


}
