css:
@-moz-document domain('www.kaskus.co.id'), domain('fjb.kaskus.co.id') {

/*
 *
 * @name        Kaskus' Skin
 * @description Make shitty Kaskus' New UI (2015) more readable, also make it less shit.
 * @author      Volc
 * @version     1.2.4
 *
 */

  /*Menghapus yang gak berguna*/
  .bottom-frame, .kasad-h, #c-thread, #sell, .banner-top-ads, .col-sm-3.sidebar, .row.sidebar-wrap, .related-thread.nongol, .popover.fade.bottom.in, .kaskus-ads.postlist, .row.ads-lb-wrap, .col-sm-12.banner-frame, .user-control-stick, #thread_post_list > .row > .col-xs-12 > .text-center, .skin, #feedback {
    display:none;
  }
  
  /*Post*/
  .main-content {
    width: 900px;
    margin-left: auto;
    margin-right: auto;
    float: none;
  }
  .quick-navigation {
    display:none;
  }
  .postlist {
    margin-bottom: 0px;
    border: none;
  } 
  .postlist .author, #forum-thread-top, .listing-wrapper .footer, .thread-prevnext .next-thread > div, #breadcrumbs-wrap, footer.site-footer, .forum-control {
    border: none;
  } 
  .col-xs-12 {
    min-height: 0px;
  }
  .site-header.scrolled {
    box-shadow: none;
  }
  
  /*Edit profile post*/
  .user-avatar{
    height: auto;
    width: auto;   
  } 
  .postlist .author .photo {
    max-height: 110px;
    max-width: 110px;
    height: auto;
    width: auto;
  } 
  .user-info > div {
    display: block !important;
  }
  .user-info { 
    font-size: 0px; 
  }
  .user-info > .title, .user-info > .meta { 
    font-size: 12px; 
    float: left; 
  }
  .user-info > .meta { 
    clear: both; 
  }
  
  body.response #thread_post_list .row.nor-post .postlist .author {
    width: 140px;
  }
  body.response #thread_post_list .row.nor-post .postlist .author .photo {
    max-width: 117px;
    max-height: 117px;
  }
  body.response #thread_post_list .row.nor-post .postlist .user-avatar {
    width: auto;
    height: auto;
  }  
 .user-avatar {
   border: 1px solid #FFF;
  }
  
  /*Mengedit header/footer bar*/
  .site-header {
    opacity: 0.8;
  }
  footer.site-footer {
    opacity: 0.8;
  }
  .dropdown-menu {
    border: none;
  }
  #k-hp-list li.selected {
    border-bottom: none;
  }
  
  /*background image*/  
  body{
    background-image: url('http://puu.sh/hrlu9.png');
    background-size: 400px;
    background-repeat: no-repeat;
    background-position: right -6.5% bottom 10%;
    background-attachment: fixed;    
  }
  
  /*Font*/
  .postlist .entry, span {
    font-size: 12px;
  }  
  .title, .userid, .user-info > .meta, .postlist, .entry-date, .description, .entry-post > .entry-content, .polling-wrapper, .listing-wrapper-user-content, .group-desc {
    color: #484848;
  }
  body, span {
    font-family: helvetica;
  }
  
  /*for you're waifu*/
  .listing-wrapper {
    margin-top: 100px;
  }
  
  /*fixed shitty design*/
  .head-title, .highlights .hot-thread ul.list-top-thread, .main-bar .two-col > div ul, .regional .top-location ul li a, .uniqness, #refine-search .categories-wrap, .message-page .message-con .list-message{
    background:#f7f7f7;
  }
  .highlights .hot-thread ul.list-top-thread, .uniqness{
    margin: 0px;
  }
  
  /*Pop "What's new" ricing because why not?*/
  .popover-notice .popover.bottom {
    box-shadow: none;
    border: none;
  }
  .popover-notice .popover.bottom .popover-title, .popover.bottom > .arrow {
    border: none;
  }
  
  /*hamburger button*/
  #left-nav.full-show, #left-nav.full-show .hover-sidebar-content {
    box-shadow: none;
  }
  
  /*KQR*/
  .goog-btn-default, .goog-btn-default:hover {
    border: 1px solid transparent;
    box-shadow: none;
  }
  .xkqr {
    border: none;
    margin-bottom: 0px;
  }
  .xkqr .entry-head, .twt-glow:hover {
    box-shadow: none;
  }
  .xkqr .markItUpHeader {
    border: none;
  }
  .kqr-editor, #box_preview {
    font-size: 12px;
    font-family: helvetica;
  }
  .thread-head, .thread-head .header-list-cat, #forum-thread-list-top, .post-entry .listing-table table td, .table > tbody > tr > td, .post-entry {
    border: transparent;
  }
}

  /*show_post css edit*/
@-moz-document regexp('http://www\\.kaskus\\.(com|co.id)/show_post/.*'), regexp('https://www\\.kaskus\\.(com|co.id)/show_post/.*') {
    body.response #thread_post_list .row.nor-post .postlist .entry-body {
    width: 87.7%;
  }
}
