css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("smart-lab.ru") 
{
    body 
    {
        font: 10pt Verdana,sans-serif;
        font-family: Arial,Helvetica,sans-serif;
        font-size: 14px;
        line-height: 1.5em;
        color: #282828;        
        background: none;
    }

    .topic h1 a 
    {
        color: #2D7AFF;
        text-decoration: none;
        font-weight: bold;
    }    

    #header, #footer, #inindex,
    .block, .console_box, .tagb,
    .sidebar_left, 
    .sidebar_right,
    .topshare,
    .block.blogs ul.list,
    #wrapper div.content_desc,
    #advAdditionalContainer,
    .toolbar a.scrollUp, 
    #registration_btn
    {
        display: none;
    }
    
.white-back {
    background: none repeat scroll 0% 0% white;
    margin: 0px 20px 30px;
    padding-bottom: 15px;
    padding-left: 20px;
}    
    a, div.trt a, div.sme, .block.blogs ul.list li a, 
    .topic h1 a.blog,
    .topic h1 span,
    .topic h1 a.blog
    {
        color: #2D7AFF;       
    }
    
}
