css:
@-moz-document regexp("https?:\\/\\/(?!.*(m|i|alpha|new)\\.).*\\.reddit\\.com\\/.*") {
body:not(.submit-page) .content .md,
.entry .md,
form.submit textarea[name=text]
{
    font-size: inherit;
	line-height: normal;
}
body:not(.submit-page) .content .md p,
.entry .md p,
form.submit textarea[name=text]
	{ line-height: normal; }
body:not(.submit-page) .content .md pre>code,
.entry  .md pre>code,
form.submit textarea[name=text]
	{ font-size: small; }

/* Headers */
body:not(.submit-page) .content .md h1,
.entry  .md h1,
body:not(.submit-page) .content h2,
body:not(.submit-page) .content h3,
.entry h3,
body:not(.submit-page) .content h4,
.entry h4,
body:not(.submit-page) .content h5,
.entry h5,
body:not(.submit-page) .content h6,
.entry h6
{
	line-height: normal;
	margin-bottom: 0!important;
}

body:not(.submit-page) .content .md h1,
body:not(.submit-page) .content .md h2
    { font-size: 1.15em; }

body:not(.submit-page) .content .md h3,
body:not(.submit-page) .content .md h4
     { font-size: 1.10em; }

body:not(.submit-page) .content .md h5,
body:not(.submit-page) .content .md h6
    { font-size: 1em; }
}
