css:
@-moz-document url-prefix("http://steamcommunity.com/market") {
body {
	width: 100%; 
    overflow-x: hidden;
    font-size: 20px;
}

.content, .market_header_title, .market_section_title, .marker_search_sidebar_section_tip, #moreInfo, #market_search_advanced_show, #sideBar, .market_large_tab_well {
	visibility: hidden;
    height: 1px !important;
}
  
.user_info, #findItems, #browseItems {
	border: none !important;    
}
  
#BG_top, #footer, .footer_content {
    height: 1px !important;
  	margin-top: -20px;
  	visibility: hidden;
}
  
.pick_and_sell_button {
	margin-right: 270px;   
}
    
.user_info {
    z-index: 99;
    margin-top: -8px;
    background-color: transparent !important;
    margin-right: -300px;
}

#sellListings {
	width: auto; 
	font-size: 20px;
}

#BG_bottom, .market_page_fullwidth.market_listing_firstsection {
	width: 100%; 
}
}
