@-moz-document domain("kaskus.co.id") {
body.fjb,body.inner,body.landing{background-color:#343A3A!important}#bottom-leaderboard,#feedback,#leader-banner,#thread_post_list .text-center,.kaskus-ads,.related-thread,body.forum .col-sm-3.sidebar{display:none}body.forum.inner #lb-button-group{position:absolute!important;right:0;top:28px;z-index:1;width:150px}body.forum.inner #lb-button-group .pull-left{float:none!important;margin-bottom:10px}body.forum .main-content,body.forum .user-control-stick{width:1024px}body.forum .main-content{margin:0 auto;float:none}.main-content-full{background-color:#f7f7f7!important}body.forum div[itemprop=text]{color:#000}body.forum .postlist{overflow:hidden;position:relative}body.forum .postlist .entry-head{background-color:#31a3ef!important;border-left:0}.permalink a,body.forum .entry-date,body.forum .meta-check i{color:#fff!important}body.forum .postlist .entry{margin-top:15px;margin-bottom:50px;padding:5px;min-height:280px}body.forum .postlist .author{width:200px;height:100%;position:absolute!important;z-index:1;border-right:1px solid #d4d4d4;border-bottom:1px solid #d4d4d4}body.forum .postlist .author figure{height:100px!important;width:100px!important}body.forum .postlist .author .photo{max-height:100px!important;max-width:100px!important;width:100px!important;height:100px!important}body.forum .postlist .user-avatar{width:100px;height:100px}body.forum .postlist .author .user-details .reputation,body.forum .postlist .author .user-details .user-info>div,body.forum .postlist .user-name{display:block}body.forum .postlist .author .user-details{color:#000}body.forum .postlist .author .user-details .nickname{font-size:14px}body.forum .postlist .reputation{margin-left:2px}body.forum .postlist ul.user-option{left:0;bottom:40px}body.forum .postlist .entry-body{margin-left:205px}body.forum .postlist .user-tools a{line-height:28px;font-weight:700;font-size:12px;display:inline-block;margin-bottom:0;text-align:center;vertical-align:middle;cursor:pointer;border:1px solid transparent;white-space:nowrap;-moz-user-select:-moz- none;-ms-user-select:none;-webkit-user-select:none;user-select:none;color:#fff;background:#1998ed;border-radius:2px;-moz-transition:all .1s ease-in-out;-webkit-transition:all .1s ease-in-out;transition:all .1s ease-in-out;text-decoration:none}body.forum .postlist .user-tools a:hover{text-decoration:none;background-color:#31a3ef!important}body.forum .postlist .user-tools a i{border-right:solid 1px #fff;float:left;line-height:28px;margin-left:-10px;margin-right:10px;padding:0 10px}body.forum .postlist .user-tools a.btn-orange{background-color:#f6a049!important}body.forum .postlist .entry-footer{margin-left:200px}body.forum .postlist .entry-footer .app,body.forum .postlist .entry-footer .last-edit{margin-left:10px;margin-top:5px}body.forum .postlist .entry-footer .last-edit{font-size:14px}body.forum .postlist .user-quickview{min-width:350px}
}
