css:
/* i really want this to be global */
.index-image{
    display: none !important;
}
#left-frame {
top: 45px !important;
width: 265px !important;
z-index: 999 !important;
left: 5px !important;
}
#left-frame .nav-tabs {
background: #ccc !important;
border: 1px solid #ddd !important;
padding: 2px 2px 2px 2px !important;
}
.index-list-li .index-title {
height: 21px !important;
margin: auto 0 auto 0 !important;
word-break: break-all !important;
overflow: hidden !important;
}
#sidebar {
    display: none !important;
}
#right-container {
margin-right: 0px !important;
        position: relative;
    top: -50px;
}
.index-list-li .index-title .altsayfa {
    display: none !important;
}
.index-list-li .index-title small {
	top: 6px !important;
	background-color: #E1E1E1 !important;
	color: #020202 !important;
}
#categories {
    display: none !important;
}
#ustbar {
font-size: 13px !important;
top: 0px !important;
position: fixed !important;
right: 0 !important;
left: 0 !important;
z-index: 1030 !important;
min-height: 0 !important;
border: none !important;
border-radius: 0 !important;
background-color: #2C3856 !important;
}
#content {
margin: 100px 0 0 180px !important;
width:92% !important;
}
#ustbar_wrap {
background-image: none !important;
}
.pagination {
display: none !important;
}
#main-footer {
display: none !important;
}
.altsayfa_header_arkaplan #main-wrap {
margin-top: 0px !important;
}
.entry-list.sub-list {
display: none !important;
}
.btn,.paylas {
font: 8pt Arial,sans-serif !important;
text-align: center !important;
white-space: nowrap !important;
cursor: default !important;
background-color: #566484 !important;
color: white !important;
border: 2px outset #a6b4d4 !important;
vertical-align:middle !important;
display: table-cell !important;
}
.entry-tablar {
display:none !important;
}
#sol_kutu{
    display:none;
}
#left-frame .nav-tabs {
    border:none !important;
    padding:0 !important;
}
#main-wrap {
    width: 90% !important;
}
.slide_detay .paylas{
font: 8pt Arial,sans-serif !important;
text-align: center !important;
white-space: nowrap !important;
cursor: default !important;
background-color: #566484 !important;
color: white !important;
border: 2px outset #a6b4d4 !important;
vertical-align:middle !important;
display: table-cell !important;
}
#category-list, #ustbar .navbar-inner, .ustbar-altsayfa-inner{
    width:100% !important;
}
