css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("freggers.de"), domain("freggers.com") {  



/* Freggers Logo */
.season-sb-header-img {background-image:url(https://www.bilder-hochladen.net/files/kp7w-7-0bcb.gif); border-radius: 12px;}

div#screenshot-box {
    background-image: url("http://www.bilder-hochladen.net/files/big/h1op-d0-821f.jpg");
    background-repeat:no-repeat;
    border-radius: 140px;
    background-position:90px 100%; 
    width: 800px;
    height: 575px;
    position: relative;
    padding-top: 45px;
    margin-top: 20px;
}

.season-sb-header {background-color:#ffff00;}

/* Freggers Menübereich Background */
/* #standard {background:url(http://files.homepagemodules.de/b587177/a_390_f822d341.jpg) 0 0 repeat;} */ 
   #standard {background-color:#898989;}

/* Freggers Taschenbereich Background */
/* #iv-content {background:url(http://files.homepagemodules.de/b587177/a_390_f822d341.jpg) 0 0 repeat;} */  
   #iv-content {background-color:#898989;}

/* Freggers Freundesliste Background */
/* #friendlist-div {background:url(http://files.homepagemodules.de/b587177/a_390_f822d341.jpg) 0 0 repeat;} */
   #friendlist-div #iv-content {background-color:#898989;}

/* Menue-Start*/
#menu-container .start {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;} 
#menu-container .profile {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#selected-menu-item.messages {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#menu-container .news {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#menu-container .badges {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#menu-container .ranks {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#menu-container .popular-places {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#selected-menu-item.search {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#selected-menu-item.account {background-color:#5a71f5;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
#selected-menu-item.support {background-color:#ffb800;border-radius: 5px;box-shadow: 3px 3px 3px #333333;}
.logout > a:nth-child(1) {background-color:#fffa00; color: #0c14f2;font-size: 12px;border-bottom-right-radius: 9px;border-bottom-left-radius: 9px;}

 .messages > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .profile > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 55, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;} 
  li.start > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .news > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .badges > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .ranks > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 55, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .popular-places > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .search > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .account > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}
  .support > a:nth-child(1){background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.1);transition: #6363CE 0.2s ease 0s, color 0.2s ease 0s;}

/* Fanprojekt Link im Menü*/
div.par:nth-child(20) > a:nth-child(1) {font-size: 10px;}

/* x_weitere Gegenstände*/
  div.par:nth-child(4) > a:nth-child(1) {font-size: 10px;}
  #iv-gifts > div:nth-child(3) > a:nth-child(1){font-size: 10px;}

/* Menue-Pull-Down*/
#nav-menu {background-color:#898989;}

/* LOG und Ragio Button*/
   #sb-log{background-color:#ffb800; color: #0c14f2;}
   #sb-radio{background-color:#ffb800; color: #0c14f2;}

/* Texte Farben */
a {background-color:#898989; font-size: 13px; color: #f4f704; text-shadow: 3px 3px 3px #333333;}

/* Pfeil und Stern */
#rb-arrow > a:nth-child(1) {background: transparent !important;}
#play-free > a:nth-child(1) {background: transparent !important;}

 /* Avatar */  
div.iv-avatar { border-radius: 12px;border-radius: 5px;box-shadow: 3px 3px 3px #333333;border: 1px solid #E3BA58;}  
div.pr-avatar-icon {background: none ;border: 0px}
 /* Fregger-Klamotten */   
div#av-clothes {box-shadow: 3px 3px 3px #333333;}   

/* Fregger-Tasche */     
div.iv-bag-container {box-shadow: 3px 3px 3px #333333;} 

/* Freunde-Selector */   
div.fl-select-box {box-shadow: 3px 3px 3px #333333;}   

/* Post-Zeiger auf Null */   
div.inbox-lst-par div.inbox-lst-msg-point {background: url("http://www.freggers.de/static/img/icon/sb/point.gif") no-repeat scroll 0px 0px transparent;height: 0px;width: 0px;} 

/* Neues-Zeiger auf Null */   
div.str-msg-point {background: url("http://freggers.de/static/img/icon/sb/point.gif") no-repeat scroll 0px 0px transparent;width: 10px;height: 20px;}    

/* Fregger-Avatar gross ändert auch Startseite*/    
/* a img { border-radius: 12px;background-color:#5a71f5;border-radius: 12px;box-shadow: 3px 3px 3px #333333;border: 3px solid #E3BA58;} */

/* Freggers Startseite Background */
.container-0 {background-color:#545454;} 

/* LOG Text User */   
div#sb-console a {background-color:#bcbaba; color: #f9fc07;font-size: 12px;}

/* Aktion Button */ 
div.pr-avatar-box a.action-button {text-shadow: 2px 2px 2px #aba8a8;}

 /* Error Text */
.error {color: #DE3A25;text-shadow: 3px 3px 3px #333333;} 

/* Werbung zurück zu.. */  
 .content-box a {color: #fff800;font-size: 20px;} 
 .content-box a:hover {color: #000000;font-size: 20px;}  

/* Startbutton Tasche */
#menu-container .start {background-image: url("http://files.homepagemodules.de/b587770/a_170_c8a825ea.png");}

 div#screenshot-box img {
    border-radius: 0px 0px 16px;
    width: 0px;
    height: 0px;}

/* Logoutseite */
   .par > a:nth-child(1){position:absolute; top:5px; font-size: 12px;}

/* FAQ-Fregger-Fans*/
   div.par:nth-child(12) > b:nth-child(2) > a:nth-child(1) {background-color:#fffa00; color: #0c14f2;font-size: 12px;}

/* Lade Freunde ein */
div.par:nth-child(3) > h2:nth-child(1) > a:nth-child(1) {background-color:#fffa00; color: #0c14f2;font-size: 14px;text-shadow: 1px 1px 1px #858383;}

div#play-free {display: block;
  height: 139px;
  background: transparent url(http://www.bilder-hochladen.net/files/big/h1op-dc-ab81.gif) no-repeat;border-radius: 70px!important;
  width: 139px;}  
   
div#play-free > a:nth-child(1){display: block;
  height: 1px;
  background: transparent url(http://www.freggers.de/static/img/start/black/play_free_de.png) no-repeat;
  width: 1px;}  
   
div#play-free > a:nth-child(1) > img:nth-child(1){display: block;
  height: 1px;
  background: transparent url(http://www.freggers.de/static/img/start/black/play_free_de.png) no-repeat;
  width: 1px;}  

/* Anmelde Pfeil */
#rb-arrow > a:nth-child(1) > img:nth-child(1) {opacity:0.3;filter:alpha(opacity=30); /* For IE8 and earlier */}
#rb-arrow > a:nth-child(1) > img:nth-child(1):hover{opacity:1.0;filter:alpha(opacity=100); /* For IE8 and earlier */}

   /* Anmelde Freggerbilder */
#female-img{opacity:0.3;filter:alpha(opacity=30); /* For IE8 and earlier */} 
#female-img:hover{opacity:1.0;filter:alpha(opacity=100); /* For IE8 and earlier */}
   
#male-img {opacity:0.3;filter:alpha(opacity=30); /* For IE8 and earlier */} 
#male-img:hover{opacity:1.0;filter:alpha(opacity=100); /* For IE8 and earlier */}

/* Freggers Logo */
 .freggers-logo {-webkit-transition: all 3s ease;/* Safari 3.2+, Chrome */-moz-transition: all 3s ease;/* Firefox 4-15 */-o-transition: all 3s ease;/* Opera 10.5-12.00 */transition: all 3s ease;/* Firefox 16+, Opera 12.50+ */}
.freggers-logo:hover {border: 40px solid #5776bd;border-radius: 20px;background: #f3f128;color: #5776bd;padding: 56px 0 0 36px;}
}
